INSERT INTO `ebooks` (`id`, `no`, `penulis`, `tajuk`, `description`, `file_path`, `muka_surat`, `perkataan`, `harga_rm`, `genre`, `bulan`, `tahun`, `penerbit`, `created_at`, `uploaded_by`) VALUES
(1, 1, 'Raisya Azliey', 'CINTA VERSI DIA', 'Rafael dengan Fia Zinnirah. Mereka couple sejak zaman sekolah. 7 tahun lamanya cinta bersemi. Mereka sepakat mengikat cinta dengan ikatan perkahwinan. Namun tinggal 4 hari sahaja lagi majlis menyatukan mereka bakal berlangsung, Rafael tiba-tiba mahu undur diri dan membatalkan majlis itu. Dengan alasan yang paling Fia tak boleh terima. \r\nHari yang sama Rafael pergi dari hidupnya, takdir tiba-tiba bawakan seorang lelaki kacak bergaya bernama Fareel Arjuna lengkap bersama tawaran yang tidak terjangkau dek akal. \r\nOleh kerana terdesak, Fia terima tawaran Fareel tanpa mengetahui siapa sebenarnya lelaki itu. Bagi Fia, dia menganggap lelaki itu kiriman Tuhan yang tiba tepat pada waktunya untuk menyelamatkan maruah dia dan keluarga akibat perbuatan tak bertanggung jawab Rafael. \r\nKata orang, cinta boleh dipupuk. Walau tidak mengenali Fareel sebelum ini, dia tekad memupuk cinta buat lelaki yang sudah bergelar suaminya itu. Sayangnya harapan Fia tak kesampaian. Cinta yang dia hulur dipandang sepi sang suami bidan terjun itu. Sikap dingin Fareel terhadapnya setiap hari buat dia makan hati. \r\nPuas dipikat puas digoda. Fareel tetap beku bak ais. Bahagia yang didamba makin tak tergapai. Dalam derita, Fia cuba bertahan demi rumahtangganya. \r\nKemunculan seorang lelaki di rumah mereka pada suatu malam yang memperkenalkan diri sebagai kawan baik Fareel iaitu Benazir Luqman, membongkar rahsia besar Fareel. \r\nKenyataan yang terhidang depan mata membuatkan Fia menggigil. Fia lari membawa hati yang parah. \r\nSaat berjauhan daripada suami, sekali lagi takdir datang menduga Fia. Pertemuan semula dengan Rafael menambah sengsara yang sedia ada. Lebih memeritkan apabila mengetahui cerita sebenar daripada mulut Rafael. Benci dan simpati bersatu dalam diri. \r\nRafael dan Fareel. Antara cinta, benci dan simpati. Fia perlu memilih salah satu. Demi bahagia yang didamba.', '../ebooksimage/687d47474523a_CINTA VERSI DIA.jpg', 301, 0, 12.00, '0', '4', 2025, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(2, 2, 'Raisya Azliey', 'EX SKANDAL SEBELAH RUMAH', 'Rafael dengan Fia Zinnirah. Mereka couple sejak zaman sekolah. 7 tahun lamanya cinta bersemi. Mereka sepakat mengikat cinta dengan ikatan perkahwinan. Namun tinggal 4 hari sahaja lagi majlis menyatukan mereka bakal berlangsung, Rafael tiba-tiba mahu undur diri dan membatalkan majlis itu. Dengan alasan yang paling Fia tak boleh terima. \r\nHari yang sama Rafael pergi dari hidupnya, takdir tiba-tiba bawakan seorang lelaki kacak bergaya bernama Fareel Arjuna lengkap bersama tawaran yang tidak terjangkau dek akal. \r\nOleh kerana terdesak, Fia terima tawaran Fareel tanpa mengetahui siapa sebenarnya lelaki itu. Bagi Fia, dia menganggap lelaki itu kiriman Tuhan yang tiba tepat pada waktunya untuk menyelamatkan maruah dia dan keluarga akibat perbuatan tak bertanggung jawab Rafael. \r\nKata orang, cinta boleh dipupuk. Walau tidak mengenali Fareel sebelum ini, dia tekad memupuk cinta buat lelaki yang sudah bergelar suaminya itu. Sayangnya harapan Fia tak kesampaian. Cinta yang dia hulur dipandang sepi sang suami bidan terjun itu. Sikap dingin Fareel terhadapnya setiap hari buat dia makan hati. \r\nPuas dipikat puas digoda. Fareel tetap beku bak ais. Bahagia yang didamba makin tak tergapai. Dalam derita, Fia cuba bertahan demi rumahtangganya. \r\nKemunculan seorang lelaki di rumah mereka pada suatu malam yang memperkenalkan diri sebagai kawan baik Fareel iaitu Benazir Luqman, membongkar rahsia besar Fareel. \r\nKenyataan yang terhidang depan mata membuatkan Fia menggigil. Fia lari membawa hati yang parah. \r\nSaat berjauhan daripada suami, sekali lagi takdir datang menduga Fia. Pertemuan semula dengan Rafael menambah sengsara yang sedia ada. Lebih memeritkan apabila mengetahui cerita sebenar daripada mulut Rafael. Benci dan simpati bersatu dalam diri. \r\nRafael dan Fareel. Antara cinta, benci dan simpati. Fia perlu memilih salah satu. Demi bahagia yang didamba.', '../ebooksimage/687d48c4e7595_EX SKANDAL SEBELAH RUMAH.jpg', 307, 0, 12.00, '0', '3', 2024, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(3, 3, 'Raisya Azliey', 'SAYANG', 'Erlisa. Hidupnya tak pernah sunyi dari luka dan kecewa. \r\n\r\nMemilih menerima calon yang dipilih keluarga sendiri sebagai suami, dia ingat dia akan bahagia. Rupanya restu mak ayah bukan jaminan bahagia itu akan kekal menjadi miliknya. \r\n\r\nKecundang dengan Qalif, jodohnya yang kedua bertandang. Dengan Khalish, kawan baik sendiri.\r\n\r\nTapi sayang, lelaki sebaik Khalish pun boleh berubah jadi setan. Anak-anak yang tak berdosa jadi mangsa. \r\n\r\nBagi Erlisa, cukuplah. Cukuplah derita dan luka itu! Lelaki semuanya sama.\r\n\r\nNamun kehadiran Ryan Mikael, si bujang terlajak yang baik saat hati dia betul-betul terluka seakan memberi nafas baru dalam hidupnya. \r\n\r\nKetika dalam hati mula berputik satu rasa indah terhadap Ryan Mikhael,  buat sekian kali dugaan datang lagi. \r\n\r\nMasih adakah serpihan bahagia untuk seorang Erlisa kecapi? Atau dia perlu membuang semuanya. Biarkan rasa itu pergi.', '../ebooksimage/687d49156d8e5_SAYANG.jpg', 344, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(4, 4, 'Raisya Azliey', 'THANKS BOSS', 'Isla Nur Airis - Cinta pandang pertamanya kecundang. Pelamin yang sering menghiasi angan dan mimpinya lenyap bersama hilangnya Luqman Anaqi. Tanpa angin ribut, cinta mereka ternoktah.\r\n\r\nMengusung duka ditinggalkan Luq, mujur ada Muammar Gheza. Bos kacak yang seringkali terlebih dos dalam memberi perhatian itu membuatkan jiwa Isla terbuai tanpa sedar. \r\n\r\nNamun melihat kemesraan Gheza dengan kekasih hatinya si model jelita Dhia Darwina, membangkitkan Isla dari mimpi indah. Sedar antara dia dan Gheza adalah mustahil, segala perasaan yang mula mekar itu cuba dikikis. \r\n\r\n\"Jangan tunjuk baik sangat boleh tak bos?\" - Isla\r\n\r\n\"Kenapa? Awak takut jatuh cinta ke?\" - Gheza.\r\n\r\nNamun apa yang cuba dielakkan itu, terjadi juga. Kerana hati sudah terlanjur suka dan cinta, Isla tidak melepaskan kesempatan yang melintas depan mata meskipun batas waktu terhad.\r\n\r\n\"Kau gila ke Isla? Kau tahu tak semua ni akan makan diri kau balik?\" - Jaja\r\n\r\n\"Sebab aku dah terlajak, Jaja. Aku tak dapat kawal. Aku nak bahagia.\" - Isla \r\n\r\nAkankah bahagia yang didamba seorang Isla itu kekal menjadi miliknya?', '../ebooksimage/687d495a5822e_THANKS BOSS.png', 280, 0, 30.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(5, 5, 'Raisya Azliey', 'KAHWIN BFF', 'Noah Adhwa.  Baginya bila Medina Ezzara itu best friend forevernya, mestilah dia kena jaga elok-elok. Tak boleh lepas tangan. Tak boleh abai walau sekejap. Malah dia rasa Medina itu dibawah tanggungjawabnya.\r\n\r\nNamun sikap terlebih menjaga si Noah buat Medina rimas. Mana tak rimas, kalau sampai nak bercinta pun Noah kawal. Hinggakan mana-mana lelaki yang datang mendampingi Medina menghulur cinta, semua Noah reject. Konon tak sesuai. Tak layak untuk gadis sebaik Medina. Mana-mana lelaki yang nak mengorat Medina, semua nampak jahat di mata Noah. Semua tak baik. Itu tak kena. Ini tak kena. Ada saja hal yang Noah timbulkan hingga penyudahnya masing-masing putus harap dan  tinggalkan Medina. Cinta tak sempat berputik pun.\r\n\r\n Disebabkan sikap terlalu mengawal dan terlebih menjaga itu lah, bila muncul gadis bernama Viyana yang jatuh cinta terhadap Noah dan cuba mengorat Noah, Medina paksa habis-habis Noah supaya terima Viyana. \r\n\r\nMedina ingat bila Noah dah couple dengan Viyana, ada peluang la dia pun merasa bercinta. Rupanya tak!\r\n\r\n\r\n\r\n Noah macam tu jugak tak berubah. Bertambah peliknya dia layan Viyana tak serupa kekasih langsung. Lebih endah tak endah dan tak ambil peduli. Hanya Viyana seorang yang pulun meraih perhatian Noah hingga kemuncaknya Viyana bermusuhan dengan Medina lantaran menganggap Medinalah punca hubungan dia dan Noah hambar.\r\n\r\nDalam kemelut perang perasaan tiga insan itu, muncul Dato\' Ed, lelaki kasanova yang Noah gelar bapak setan. Mahu melamar Medina  sebagai isteri. Isteri kedua! Kerana menurut Dato\' Ed isteri pertamanya sudah \'tidak berfungsi\'.\r\n\r\nBila Noah dapat tahu lamaran si bapak setan itu, kalut dia ajak Medina kahwin. Dengan alasan yang tak masuk akal langsung.\r\n\r\n\"Macam ni. Kita kahwin dulu. Sambil tu, aku carikan kau lelaki yang betul-betul sesuai dengan kau. Bila dah jumpa lelaki tu, kau suka dia, dia suka kau, aku lepaskanlah kau. Selagi belum jumpa, selagi itu kau tanggungjawab aku.\"\r\n\r\nMedina ternganga dan rasa tak mampu percaya dengan apa yang dia dengar. Perancangan gila apa ni?\r\n\r\n\"Kahwin bukan main-main, bro. Apahal kita nak kena kahwin dulu sementara kau cari lelaki yang sesuai dengan aku? Isy, aku...\" \r\n\r\n\"Aku nak jaga kau. Aku sayang kat kau D. Aku takut kau silap jumpa lelaki yang sesuai dengan kau. Aku tak mahu kau dipermainkan. Lagi-lagi yang spesies bapak setan tu. Kalau kita kahwin, 24 jam kau tu tanggungjawab aku. Kau ada depan mata aku.\"\r\n\r\nTak pernah lagi Medina jumpa bff macam Noah. Terlampau menjaga sampai ajak kahwin. Sungguh akal dia sukar untuk terima semua tu.\r\n\r\n\"Kita tak boleh kahwin.\"\r\n\r\n\r\n\r\nYang seorang mahu. Yang seorang bersungguh menolak. \r\n\r\nTapi kata Noah kepada Medina, kisah kita takkan pernah habis.\r\n\r\nAkankah perjalanan kisah best friend forever Noah Adhwa dan Medina Ezzara mampu kekal manis dan indah?', '../ebooksimage/687d49cbb969b_KAHWIN BFF.png', 331, 0, 36.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(6, 6, 'Raisya Azliey', 'MY500K HUSBAND', 'Pertemuan demi pertemuan dengan seorang lelaki tidak dikenali menimbulkan curiga di hati Ruby Adriana.\r\n\r\n\"Encik stalk saya?\" - Ruby Adriana\r\n\r\n\"Ini tandanya kita ada jodoh.\" - Ilhan Ghiffari\r\n\r\nRuby Adriana langsung tak kenal siapa lelaki itu. Peliknya selamba pulak lelaki tu ajak kahwin.\r\n\r\nRuby Adriana @ Baby Adriana. Dia bukan tak nak kahwin. Dia cuma nak enjoy, kerja, kumpul harta dulu. Lepas tu baru fikir pasal jodoh. Masalahnya makngah dia pula tak senang duduk selagi dia tak kahwin.\r\n\r\n\"Tahun lepas, tak nak. Dua tahun lepas pun sama. Okay, dua tahun lepas tu, baby tengah study. Final year kan. Makngah faham yang tu. Ni, tahun ni. Baby 25 tahun dah, nak. Ada orang berkenan, apa nak tangguh lagi? Ke nak tunggu yang dalam tu kerepot baru kamu nak?\" - Makngah\r\n\r\nAmboi makngah punya ayat. Tak beragak!\r\n\r\nRupanya lelaki yang dikata stalk Ruby Adriana, itulah calon jodoh yang makngah cari. Patutlah lelaki tu pulun ajak dia kahwin. Semua ni makngah punya rancangan.\r\n\r\nParah! Pening Ruby Adriana cari jalan nak reject calon jodoh dia yang ke sebelas ni. Tapi tiba-tiba idea kecemasan dia melintas.\r\n\r\n\"Kalau encik boleh bagi dekat saya ringgit malaysia lima ratus...\" - Baby Adriana\r\n\r\n\"Sekarang pun saya boleh bagi, baby.\" - Ilhan Ghiffari\r\n\r\n\"K! Lima ratus k. Boleh bagi lima ratus k dekat saya, baru saya terima encik sebagai jodoh saya.\" - Baby Adriana\r\n\r\nKembang hati Ruby Adriana. Yakin idea kecemasannya berkesan. Pasti Ilhan Ghiffari cancel ajak dia kahwin. Putus harap!\r\n\r\nMalangnya lain yang disangka, lain pula halnya yang jadi.\r\n\r\n\"Baby, bak sini nombor akaun. Baby Adryanna saya punya pasal, esok jugak saya uruskan lima ratus k tu ke akaun baby. Janji kita kahwin, baby. Lebih cepat lebih bagus.\" - Ilhan Ghiffari\r\n\r\nIdea kecemasan tak boleh pakai langsung!\r\n\r\n“Mak, tak nak kahwin!” – Ruby Adriana\r\n\r\n\"Baby, setakat nak enjoy, kita boleh je enjoy sama-sama. Enjoy lepas kahwin, lagi best kut. U nak kumpul duit banyak-banyak, nak jadi jutawan, yang tu anytime I boleh tunaikan. Janji u setuju dulu jadi wife I.\" – Ilhan Ghiffari\r\n\r\nYou buat I tak boleh stop mencintai you, baby.', '../ebooksimage/687d4a520588e_MY500K HUSBAND.jpg', 326, 0, 35.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(7, 7, 'Raisya Azliey', 'KAY : BUKAN SATU MALAM', 'Kayla dan Firdhan. Kisah mereka bermula pada malam pertama perkahwinan Firdhan sendiri. Siapa sangka, malam yang sepatutnya dinikmati penuh keindahan tidak seperti yang diharap.\r\n\r\nFirdhan bercinta dengan Kaleela. Menikahi Kaleela. Anehnya yang menjelma depan mata Firdhan bukannya Kaleela tetapi Kayla. Kayla yang Firdhan belum pernah tahu kewujudannya selama ini!\r\n\r\nBila terbongkar semuanya, lebur seluruh cinta yang ada. Yang tinggal cuma dendam amarah.\r\n\r\nTekad Firdhan cuma satu. Dia akan pastikan dendamnya pada dua beradik kembar itu berbalas.\r\n\r\n“Kay kena jadi isteri I. Baru I akan maafkan perbuatan you.” – Firdhan.\r\n\r\n“You tak boleh buat I macam ni, Fir. I yang perlukan you. Bukan Kay. Kay tak layak untuk you. Kay tak sesuai untuk you, Fir. Tolong jangan buat I macam ni. I sedar I salah buat macam tu. I janji tu kali terakhir I buat.” - Kaleela\r\n\r\n“Siapa suruh you bagi dia dengan I.” - Firdhan\r\n\r\n“You dah pilih sendiri, El. You yang hancurkan.” – Firdhan\r\n\r\nWalau secubit kasih masih berbaki, bila nafsu amarah lebih menguasai, yang terfokus di hadapan mata kini cuma satu. Dendam!\r\n\r\nPuas Kayla memujuk Firdhan untuk sebuah kemaafan. Namun siapa dia untuk Firdhan menerima pujukannya mudah-mudah. Dia bukan siapa-siapa. Mereka kenal belum pun 24 jam.\r\n\r\n“Kay kena jadi isteri I. Dalam masa seminggu, Kayla Marissa akan menjadi isteri Firdhan Wafiq. Kalau tak, I akan saman you dan keluarga you satu juta ringgit.” – Firdhan\r\n\r\nTiada pilihan, sekali lagi Kayla menurut dalam terpaksa. Berkahwin dengan Firdhan demi menyelamatkan keadaan. Sukar untuk dihadap namun dia tetap akur.\r\n\r\nBerkahwin dengan Firdhan rupanya bukanlah penamat dendam Firdhan. Hari-hari Kayla berteman rantaian cerita duka. Sampai satu tahap, Kayla lelah untuk bertahan.\r\n\r\n“Berapa malam lagi Kay kena jadi isteri abang baru terhapus dendam abang pada Kak El dan baru abang dapat maafkan kami? Kay dah ikut semua kehendak abang.” – Kayla\r\n\r\nWaktu Firdhan nyenyak tidur, Kayla keluar rumah. Bawa diri. Bawa hati yang lara.\r\n\r\nKetiadaan Kayla menjadikan Firdhan tenggelam punca hilang arah.\r\n\r\n“Satu malam tu takdir kita sebenarnya, Kay. Abang sedar. Takdir nak satukan kita. Dengan cara yang kita tak pernah duga macam tu. Abang tak boleh hilang, Kay. Tolong beri abang peluang, sayang. Kalau masa boleh diundur semula, abang sangat berharap Kay yang abang kenal dulu.”\r\n\r\nDemi Kayla yang dicintai, Firdhan berusaha menjejak. Dan pencariannya ternoktah dengan satu kejutan.\r\n\r\n\"Boleh tak abang janji dengan Kay, kita bercinta sampai syurga? Kalau Kay ditakdirkan pergi dulu, Kay tunggu abang di sana.\" - Kayla.', '../ebooksimage/687d57949f703_KAY BUKAN SATU MALAM.jpg', 364, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(8, 8, 'Raisya Azliey', 'TAKE CARE, TUAN', 'Cempaka Aulia dan Ayyash Attariz. Masing-masing bergelut dengan masalah hidup yang menyesakkan. \r\nAttariz dengan kisah cik tunangnya Sherry Atilya yang kerap menimbulkan masalah semenjak mereka \'PJJ\'. \r\nCempaka pula, perempuan yang asalnya baik-baik namun dugaan menimpa apabila kesucian diri yang dipertahankan diragut oleh manusia yang paling dia percaya. Hilangnya kesucian diri, lebur jiwa dan raga. Sekelip mata warna pelangi yang menghiasi kanvas hidup Cempaka berubah hitam.\r\nPertemuan Cempaka dan Attariz suatu takdir. Attariz muncul depan Cempaka pada masa yang sangat tepat. Begitu cantik aturan Allah. \r\nKetika Attariz on the way pergi bercuti. Konon nak tenangkan fikiran. Tapi tak sempat sampai tempat bercuti pun, satu lagi masalah yang datang. Dia nampak Cempaka yang mahu bunuh diri. \r\nAttariz selamatkan Cempaka. Attariz bagi kata semangat pada Cempaka yang dah lebur semangatnya untuk hidup. Dan Attariz nekad bawa Cempaka pulang ke rumah keluarganya. Bagi Cempaka kerja sebagai pembantu rumah.\r\nDi sini kehidupan baru seorang Cempaka Aulia bermula. Dan di sinilah juga mula terlakarnya kisah hati Cempaka dan Tuan Attariznya.\r\n\"Mulai sekarang, jangan nak fikir-fikir lagi dah apa yang terjadi pada diri awak. Jangan sesekali fikir nak bunuh diri lagi. Benda yang berlaku pada awak tu bukan atas kesilapan awak. Hidup baik-baik di sini. Dalam rumah ni, takde sorang manusia pun yang tak akan sayang awak, Cempaka.\" - Attariz.\r\n\"Termasuk tuan ke? Tuan pun sayang saya?\" - Cempaka\r\n\"Ye, Cik Cempaka Aulia. Saya pun sayang awak. Dah, jangan nak emo lagi. Masalah tu awak campak je jauh-jauh. Perjalanan awak masih panjang. Apa yang jadi tu bukan penamat hidup awak.\" Attariz\r\n\"Tapi saya ni dah tak sama macam sebelum ni lagi. Ada ke lelaki yang nak beristerikan saya nanti? Tak akan ada, tuan. Orang bodoh pun tak nak beristerikan perempuan dari sisa lelaki lain macam saya ni. Semua yang saya ada dah musnah. Apa je yang saya boleh bagi dekat suami saya nanti?\" - Cempaka\r\n\"Hati awak, Cempaka. Hati awak, Allah pegang. Jaga hati awak tu baik-baik. Insyaa Allah sampai masanya pasti akan hadir lelaki yang ikhlas sayang dan terima awak seadanya. Saya yakin.\" - Attariz\r\n\"Kalau sampai saya tua tak ada lelaki nak saya?\" - Cempaka\r\n\"Saya kan ada.\" - Attariz\r\nKata-kata yang buat hati Cempaka berbunga. Kata-kata yang membuahkan harapan di hati Cempaka dalam diam. Dan ada rasa yang mula bertunas dalam hati Cempaka buat Tuan Attariznya.\r\n\"Take care, tuan. Jiwa dan raga tuan, saya sorang je yang punya.\" - Cempaka\r\nCempaka tidak segan bermain kata. Mana hati Cempaka tak bahagia bila Attariz tak pernah tak melayan setiap kata-kata yang dia lemparkan. Mana hati Cempaka tak cair bila Attariz juga bijak bermain kata dengannya.\r\n\"Awak pun, jaga mata jaga hati. Jangan cari yang lain lagi.\" - Attariz\r\nKehadiran Cempaka di rumah keluarga Attariz membuatkan seluruh hidup Attariz berubah 360 darjah. Siang malam mengadap muka Cempaka. Attariz seolah lupa dia tunangan orang. Perhatian berlebihan yang Attariz berikan pada Cempaka buat dia jadi tak kisah pada Shery lagi. Lantaklah berapa juta kali Shery nak buat hal pun. \r\nSemua ahli keluarga Attariz menyaksikan perubahan besar lelaki yang sebelum ini dikenali sebagai workaholic itu. \r\nCempaka tak pernah tahu Attariz tunangan orang. Dia tak pernah sekali pun tanyakan Attariz milik siapakah hati lelaki itu. Yang Cempaka tahu dan sedar, dia rasa bahagia tinggal di rumah keluarga Attariz. Kasih sayang Attariz dan keluarganya yang melimpah buatkan Cempaka lupa akan kekurangan dalam diri.\r\nNamun kehadiran Shery suatu hari di rumah keluarga Attariz sekaligus membongkar status sebenar diri Attariz buatkan hati Cempaka rabak.  \r\n\"Tuan tipu saya. Tuan mainkan hati saya.  Tuan tunang orang. Tuan sengaja bodoh-bodohkan hati saya sebab tahu tak akan ada lelaki nak pada saya. Kan?\" - Cempaka.\r\n\"Sumpah, saya tak berniat nak menipu awak! Awak yang tak pernah nak tanya status diri saya.\" - Attariz \r\n\"Kata-kata sayang dari mulut tuan tu selama ni dusta semuanya kan?\" - Cempaka\r\nSedar sakitnya hati mencintai lelaki yang tidak sepatutnya dia cintai, dan sedar jurang ketara antara mereka berdua, Cempaka pilih untuk keluar dari rumah keluarga Attariz.\r\nTapi kata Attariz, \r\n\"Saya ikhlas bila saya cakap saya sayang awak.\" - Attariz\r\nAttariz mengaku ikhlas. Tapi bila Cempaka soal sayang yang macam mana, Attariz membisu tanpa jawapan.\r\nKiri Cempaka. Kanan Shery. Dan Attariz tersepit ditengah-tengah.\r\nSudahnya Cempaka benar-benar pergi. Bagi Cempaka, dia tidak akan izinkan hatinya luka lagi.\r\n\"Cukuplah sekali hati saya sakit sebab terlajak mencintai lelaki yang tak sepatutnya saya cintai.\" - Cempaka\r\nBenarkah seperti mana yang dikata oleh Cempaka bahawa kata-kata sayang yang terluah dari bibir Attariz selama ini terhadapnya semua dusta belaka? \r\nBenarkah perasaan Attariz terhadap Cempaka semuanya sekadar mainan perasaannya? \r\nJika perasaan Attariz terhadap Cempaka tulus dan bukannya dusta, bagaimana dengan Shery yang sudah bergelar tunangannya? \r\nSebelum ini memang Shery seringkali main tarik tali. Bila Attariz ajak kahwin, Shery bagi macam-macam alasan mintak tangguh. Malah majlis mereka sudah tiga kali ditangguhkan. Semua atas kehendak Shery.\r\n\"I dah ready dah nak jadi wife you sekarang, b. Lepas kita kahwin, I akan balik sini, tinggal dengan you. I tak nak kita PJJ lagi.\" - Shery. \r\nYa, Shery dah bersedia. Sesuatu yang dah lama Attariz tunggu sebenarnya. Dan sepatutnya hal itu menggembirakan seorang Ayyash Attariz. \r\nNamun entah mengapa, wajah Cempaka juga yang kerapkali hadir menjentik hatinya. Buat persiapan kahwin pun, semangat Attariz seakan terbang entah ke mana.\r\n\"You pilih, b. Nak I atau perempuan kotor yang you kutip tu?\" - Shery\r\nAntara Cempaka Aulia dan Shery Atilya. Siapakah yang berjaya menakluk jiwa dan raga seorang Ayyash Attariz? Adakah Shery yang dah bertahun-tahun berada dalam carta hatinya atau Cempaka yang baru hadir dalam hidupnya belum pun setahun.', '../ebooksimage/687d4b1e344fd_TAKE CARE, TUAN.jpg', 411, 0, 35.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(9, 9, 'Raisya Azliey', 'IKAT HATI DIA', 'Raazin dan Nerissa. 15 tahun lalu takdir temukan mereka berdua. Atas kapal terbang yang sama, duduk sebelah menyebelah. Sama destinasi cuma tujuan yang berbeza. Siapa sangka perkenalan singkat itu membuahkan satu kisah panjang kemudiannya. \r\nHanya kerana salah faham, Nerissa pergi begitu sahaja. Sekeping nota yang ditinggalkan meremukkan seluruh hati seorang Raazin Aariz. \r\n\"Kisah kita adalah kisah yang tidak sepatutnya berlaku. Penyesalan dan kebodohan paling besar dalam hidup I adalah memberi seluruh hati dan kepercayaan kepada lelaki seperti you, Raazin Aariz.\" - Nerissa. \r\n15 tahun berlalu. Pada majlis pertunangan Raazin dan Lily Katrina. Takdir bawakan Nerissa semula. \r\nNerissa yang tak pernah sekali pun luput daripada ingatan seorang Raazin Aariz. Datang pada waktu yang tidak pernah Raazin jangka.\r\n\"Bakal isteri aku, bro. Nerissa Iman. Ni la perempuan yang aku selalu cerita tu. Susah nak dapat dia ni. Jenuh aku ngorat. Bertahun-tahun baru dapat.\" - Zariel \r\nPertemuan semula kali ini membuatkan Raazin sanggup bertindak diluar kewarasan akalnya biarpun menyedari  Nerissa kesayangan Zariel, sepupunya sendiri. \r\nDemi bahagia yang tertunda 15 tahun lalu, Raazin nekad \'culik\' Nerissa dan \'simpan sementara\' di rumah rahsianya sehingga Nerissa bersetuju menjadi isterinya biarpun menyedari tindakannya itu memusnahkah hati banyak pihak. Zariel dan Lily Katrina terutamanya.\r\n\"You dah gila ke? I ni bakal isteri sepupu you sendiri tahu tak?\" - Nerissa.\r\n\"I tak pedulilah you bakal isteri siapa. Yang I tahu, I akan jadi gila kalau you tak jadi wife I.\" - Raazin.\r\nNerissa merayu dilepaskan namun Raazin berkeras siap mengugut. \r\n\"I masih simpan gambar-gambar kita dekat Luar Negara dulu. Kalau you tak nak I viralkan satu dunia gambar-gambar tu, kahwin dengan I.\" - Raazin.\r\nTidak boleh tidak. Raazin tak terima sebarang jawapan penolakan Nerissa. \r\n\"Kalau you jadi isteri I, I delete semua gambar tu depan mata you sendiri. Sumpah!\" - Raazin.\r\nSudahnya diluar kerelaan hati, Nerissa terima Raazin sebagai suami. Tekadnya hanya satu. Nak hapuskan semua yang berkaitan antara dia dengan Raazin dulu. Pada Nerissa, sekali dia dah benci, sampai mati pun rasa itu tidak akan berubah.\r\nBagi Raazin pula bila Nerissa dah jadi isterinya, dia tidak akan sia-siakan peluang yang dimilikinya. \r\nCinta seorang Nerissa Iman yang pernah wujud buat Raazin Aariz 15 tahun dulu akan dipastikan wujud semula. \r\nPelbagai onak duri yang Raazin terpaksa hadap dalam mendapatkan cinta seorang Nerissa Iman semula. Nerissa yang keras hati. Nerissa yang beku walaupun dah jadi isterinya. Tidak cukup dengan itu, Zariel berkeras mahu bersaing mendapatkan Nerissa menjadi haknya. Dan Katrina pula mendesak Raazin menghawininya. \r\nRaazin tak peduli apa saja ranjau yang membadai asal Nerissa tetap kekal disisinya. Dia yakin akan sampai masanya cinta Nerissa Iman yang kembali menjadi miliknya.\r\nMalangnya keyakinan Raazin tersasar. Cukup setahun usia perkahwinan mereka, Nerissa merayu minta dilepaskan. \r\n\"Please, Raazin. I tak boleh nak teruskan. I tak boleh hidup dengan lelaki yang I tak cinta. Perkahwinan kita ni tak ada makna, Raazin. You tak rasa sia-sia ke simpan isteri yang macam I? Orang lain bahagia dilayan isteri, tapi you memendam rasa kena hadap I yang tak pernah nak layan you as a husband. You sepatutnya beristerikan perempuan yang mencintai you. Macam Katrina.\" - Nerissa\r\n\"Takkan sebesar zarah pun you tak pernah rasa cinta you terhadap I sepanjang setahun kita kahwin? I boleh nampak dalam sinar mata you. Cinta you dekat I masih ada. I love you, baby. I tak boleh hilang you lagi macam dulu. Kenapa susah sangat you nak mengaku perasaan you dekat I? Jangan sia-siakan masa yang Allah bagi kat kita. Kita berhak bahagia.\" - Raazin. \r\nKelu lidah Nerissa nak beri jawapan paling selamat. Bohonglah kalau dia cakap sebesar zarah pun perasaan cinta terhadap Raazin tidak muncul semula. \r\nBila dah jadi suami isteri, Raazin yang dia benci sekelip mata jadi seorang Raazin yang berbeza sangat. \r\nSiang malam Raazin depan mata dia. Raazin yang terlebih manis dari selori gula walau dia tunjuk beku 24 jam. Raazin yang tersangat caring. Dalam tunjuk beku, hati dia diam-diam cair. Sumpah dia jatuh cinta sekali lagi pada Raazin! Setiap sentuhan Raazin sentiasa menggetarkan jiwa raganya. Malah dia tak peduli pun yang ugutan Raazin demi nak dia terima lelaki itu semula setahun yang lalu tu tipu helah Raazin belaka. \r\nTapi bila ada banyak hati yang tak senang melihat rumahtangga mereka berdua, mana mungkin Nerissa boleh duduk diam tanpa rasa apa-apa. Tambahan lagi dia bukanlah menantu pilihan. Lantaran itu dia lebih rela pilih untuk berundur. Dia lebih rela buat Raazin patah hati dan berputus harap terhadapnya. \r\nBerjayakah Raazin mempertahankan rumahtangganya bersama Nerissa? Relakah hati seorang Raazin melepaskan Nerissa Iman pergi buat sekian kali bila getar dalam jiwa Raazin hanya mendambakan seorang Nerissa yang sama melengkapi bahagianya.', '../ebooksimage/687d4b5dce20c_IKAT HATI DIA.jpg', 408, 0, 17.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(10, 10, 'Raisya Azliey', 'CUKUP DIRINYA SAJA', 'Nur Ayra Jasmin - Sewaktu di alam sekolah menengah dulu, dia pernah jatuh cinta dalam diam terhadap lelaki bernama Syed Rafiqi yang merupakan abang kepada best friendnya sendiri, Syarifah Haslinda.\r\nSyed Rafiqi yang kacak, ditambah pula merupakan pelajar paling cemerlang di sekolah membuatkan dia diminati ramai. Bahkan bukan calang-calang juga pelajar perempuan yang mengejar cinta seorang Syed Rafiqi. Hebatnya aura seorang Syed Rafiqi sampaikan ada cikgu perempuan yang turut meminatinya.\r\nKala melihat jurang ketara dengan Syed Rafiqi, Ayra yang sedar akan kekurangan diri  memilih memendam rasa biarpun kerapkali bisikan hatinya meminta dia mencuba meluahkan semuanya terhadap lelaki itu.\r\nBagi Ayra, mana mungkin dia berani berterus-terang pada Syed Rafiqi yang merupakan pelajar paling hot di sekolahnya ketika itu. Dia dengan Syed Rafiqi macam langit dengan bumi. Akhirnya seiring peredaran masa, perasaan yang pernah wujud itu perlahan terkubur begitu sahaja. \r\nTenggelamnya Syed Rafiqi, hadir lelaki bernama Nik Armin Uwais. Perkenalan singkat menyatukan mereka berdua sebagai suami isteri. Harapan Ayra rumahtangga yang dibina bahagianya kekal hingga ke syurga.\r\nNamun, belum pun habis fasa bulan madu, dugaan membadai menggoyahkan bahagia yang baru bermula.\r\nNik rupanya bukanlah suami yang boleh Ayra gantung harap.  Bicara Nik manis. Tapi satu pun tak boleh dipakai. Spesies anak emak yang terlalu menurut telunjuk dan arahan emaknya hinggakankan hati isteri diabaikan. \r\nDibelakang emak sendiri,bicara Nik mengalahkan lori gula terbalik. Berjanji bulan dan bintang sampai Ayra terpesona. Tapi bila ada emaknya, Arya terpinggir. Malah hati Ayra sikit pun Nik tak peduli.\r\nTerbeban dengan tanggungjawab \'kena paksa pikul\' hinggakan keperluan keluarga Nik pun Ayra yang kena sediakan menggunakan duit Ayra sendiri kerana konon kata emak Nik, \r\nduit gaji Nik sikit. Tak cukup nak belanja semuanya. Dan sebagai isteri kepada Nik, tanggungjawab Ayra melengkapi apa yang tidak mampu Nik sediakan. \r\nMula-mula Ayra menurut. Namun sampai satu tahap, sabarnya melebur. Dia jadi makin tertekan. Tidak pernah menyangka perkahwinan itu rupanya satu bebanan. Bukan saling berkongsi dan saling menyempurnakan seperti mana janji Nik diawal hubungan mereka bermula.\r\nSampai satu ketika kemuncak tekanan yang dihadapi, Ayra yang tidak menyedari dirinya hamil telah mengalami keguguran.\r\nNik menuduh Ayra gugur sebab kecuaian diri sendiri. Emak mentua pula kata Ayra jenis tak pandai jaga diri, tak tahu beringat. Walhal apa yang berlaku gara-gara terlampau tertekan dan merekalah punca dia mengalami tekanan melampau.\r\nAkhirnya Ayra nekad balik rumah keluarga sendiri. Dia minta dilepaskan. \r\nBila Ayra meminta dilepaskan barulah Nik sedar erti sebenar seorang isteri disisinya. Demi menjernihkan hubungan dengan Ayra, Nik cuba berubah menjadi suami seperti yang diharap Ayra walau menyedari hati seorang isteri itu sudah tawar terhadapnya.\r\nPuas Nik pujuk Ayra ajak berdamai. Tapi Ayra jangankan nak berdamai, nak pandang Nik pun puas dipaksa-paksa. \r\n\"Beri abang peluang sekali je. Abang janji akan berubah sepenuhnya untuk Ayra. Kita bina hidup baru. Kita pindah jauh dari family abang. Lepas ni Ayra tak perlu lagi dah nak tanggung family abang.\" - Nik\r\n\"Pindah lagi? Boleh ke? Abang mana boleh berenggang dengan family abang. Kalau boleh ceruk mana kita duduk pun, abang angkut semua sekali duduk dengan kita. Abang lupa ke, Acu dengan Acik awal-awal lagi dah beritahu, kat mana abang dia orang pindah, dekat situ la dia orang akan ikut duduk.\" - Ayra\r\n\"Ayra tolong percayakan abang kali ni. Ayra bagi sekali je peluang. Please.\" - Nik\r\nNik gigih memujuk. Ayra pula makin dipujuk, bukan dia sudi hendak berlembut pun. Makin keras hati adalah. \r\n\"Peluang apa? Peluang untuk jadi bibik family abang lagi? Abang bukan perlukan isteri pun sebenarnya. Abang perlukan bibik je. Nak gajikan bibik tak mampu, abang guna cara ni. Jadikan isteri yang dikahwini sebagai bibik percuma untuk family abang.\" - Ayra\r\nSungguh Ayra benar-benar serik kali ni. Mengenang tingkah Nik yang berat sebelah, melebihkan keluarganya sendiri berbanding isteri malah sikap Nik yang terlalu mengikut telunjuk dan arahan emaknya hingga mengabaikan hati dan perasaan seorang isteri buat Ayra tak berani nak percaya dan harapkan Nik lagi. \r\nBerbaloikah Ayra bagi Nik peluang sekali lagi atau biar sahaja perkahwinan ini kecundang? \r\nKata Linda, masih ramai lagi lelaki yang lebih baik dari Nik yang boleh bahagiakan Ayra. \r\n\"Abang aku pun pernah beritahu aku, dia berkenan dekat kau, Ayra. Tapi aku tak sempat nak kenalkan kau kat dia masa tu sebab Nik laju sangat sambar kau. - Linda, best friend Ayra.\r\n\"Sejak dulu lagi saya sukakan Nur Ayra Jasmin sebenarnya. Berkali-kali saya bagi hint, tapi dia sombong dengan saya.\" - Syed Rafiqi\r\nWhat? Abang Linda, Syed Rafiqi. Gila Ayra tak terkejut dengar nama tu. Syed Rafiqi lelaki yang pernah Ayra suka dalam diam tapi tak berani nak luah isi hati. Linda sendiri pun tak pernah tahu hal tu. \r\nBila Linda berkali-kali cakap dan yakinkan bahawa Syed Rafiqi memang sukakan dia, hati Ayra terusik. Kata Linda lagi, Syed Rafiqi tu masih single.  \r\nSyed Rafiqi dan Nik Armin Uwais. Dua lelaki yang meminta hati yang sama milik Nur Ayra Jasmin. Siapakah akhirnya yang berjaya memilikinya? Adakah peluang itu milik Syed Rafiqi atau menyebelahi Nik Armin Uwais? \r\n\'Tuhan, kalau boleh aku minta. Jangan pernah hilangkan dia dari hidupku. Kerana dialah bahagiaku.\'', '../ebooksimage/687d4b983f1b9_CUKUP DIRINYA SAJA.jpg', 310, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(11, 11, 'Raisya Azliey', 'I LOVE YOU MORE', 'Amjad. Pelamin angannya musnah apabila kekasih hatinya Aara Munirah yang belajar di Luar Negara diam-diam telah memilih berkahwin dengan lelaki lain.\r\nKecewa dengan tindakan Mun, Amjad pilih berkahwin dengan Suri Dahlia yang dah lama menyimpan perasaan terhadapnya. Tujuan Amjad cuma satu. Dia mahu membuktikan kepada Mun bahawa dia juga mampu bahagia tanpa Mun.\r\nSayangnya perkahwinan Amjad dan Suri hanya cinta sebelah pihak. Bahagia yang cuba digapai semakin menjauh bila perasaan Amjad tidak pernah sekali pun wujud buat Suri. Semakin Amjad cuba mengikis bayangan dan kenangan bersama Mun, makin kuat pula hatinya mengingati Mun. Suri yang setia di depan mata, akan tetapi yang selalu difikiran tetaplah juga Mun.\r\nSudahnya Suri bawa diri bersama rajuk dihati. Dia memilih berundur kerana tidak sanggup meneruskan kehidupan bersama lelaki yang dihatinya hanya tersemat nama perempuan lain.\r\n3 tahun berpisah, takdir pertemukan semula Amjad, Suri dan Mun. \r\nMun yang sudah diceraikan suami kembali menagih semula cinta yang pernah dia persiakan. Manakala kepulangan Suri kali ini untuk memujuk Amjad demi si kecil yang sedang berjuang antara hidup dan mati. \r\nPertemuan semula mereka bertiga kali ini buatkan Amjad terduduk. Antara Mun dan Suri, dia terpaksa memilih salah satu. Pilihan yang sebolehnya tidak menimbulkan luka dihati.\r\n\"Mun dah kosong sekarang. Mun menyesal tinggalkan abang dulu. Kita mulakan balik sekali lagi ye? Kali ni Mun janji tak akan tinggalkan abang lagi.\" - Mun.\r\n\"Suri nak pinjam abang sekejap je. Demi dia. Dia tak lama atas dunia ni, bang. Dia sakit sejak lahir. Kalau boleh, sebelum dia pergi, Suri nak dia rasa nikmat kasih sayang papa yang dia tak pernah rasa.\" - Suri.', '../ebooksimage/687d4bdf07f27_I LOVE YOU MORE.jpg', 274, 0, 16.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(12, 12, 'Raisya Azliey', 'DIYA ALDRISH : PENGHUJUNG DENDAM', 'HUD ALDRISH – Businessman kacak yang datang dari keluarga berada. Pakej yang dimilikinya membuatkan ramai gadis yang mengejar mahu menakluki namun tiada seorang pun yang berjaya memiliki hati lelaki itu. Sehingga takdir membawa satu pertemuan antara lelaki itu dan si gadis bernama AUDIYA SOFEA \r\nPertama kali bertentang mata dengan si Diya, Aldrish terus jatuh cinta. Pesona seorang Diya buatkan Aldrish angau siang dan malam. \r\nDan bila hati sudah terpaut, Aldrish melamar si pujaan hatinya itu menjadi suri hidup. Kisah mereka bermula mudah dan pantas. Bercinta hanya sekejap, terus bertunang dan berkahwin.  \r\nPada diri Aldrish, Diya adalah cinta matinya. Cinta dan bahagia yang diharap akan dia bawa sampai ke syurga. Kerana Aldrish bukanlah lelaki yang mudah jatuh cinta. Dan Diya adalah gadis paling bertuah berjaya membuka hati Aldrish untuk jatuh cinta.\r\nNamun siapa sangka perkahwinan yang pada sangkaan Aldrish dibina atas dasar cinta itu rupanya menyembunyi satu rahsia besar seorang Audiya Sofea. \r\nBelum pun setahun usia perkahwinan, segalanya mulai terbongkar apabila perniagaan Aldrish diuji mengalami kejatuhan.  \r\n“I ni suami you, Diya. Masa kita bercinta you janji sanggup susah senang dengan I. Kenapa bila bisness I sampai tahap susah macam ni, sesen pun you tak boleh hulur nak tolong I? You ni  macam suka je tengok I jatuh.” – Aldrish. \r\n“Bukan macam Mr Hud Aldrish. Memang itu yang I suka pun. I dah lama tunggu nak tengok you jatuh. Dan kalau you nak tahu, tengok you \'jatuh merudum\' macam ni belum cukup pun sebenarnya untuk puaskan hati I. Ada banyak benda lagi I nak you rasa dan hadap sebenarnya.\" – Diya \r\nAldrish terngadah bila Diya balas ucapan yang tidak pernah dia jangka begitu. Isteri yang dicintai sepenuh hati sekelip mata berubah seakan menjadi perempuan lain yang tidak pernah dia kenal saat dia bukan Aldrish yang dulu. Bahagia yang sedang dikecapi tiba-tiba dirasakan  makin jauh perginya. \r\n“I tak percaya you boleh berubah macam ni, Diya.” – Aldrish \r\n“Afeeya Sahara kakak kandung I. You dah boleh percaya kut sekarang?” – Diya \r\nAFEEYA SAHARA. Mendengar sebaris nama yang meniti di bibir Diya buatkan seluruh sendi Aldrish terasa lemah. Tubuh menggigil menghadamkan kenyataan yang terhidang di depan mata. Puas dia cuba kuburkan sebaris nama itu dan satu kisah yang merantai langkahnya selama ini, kini nama itu muncul semula menghantuinya.\r\n“Afeeya Sahara binti Sahak?” Jujur Aldrish berharap sangat tekaannya salah.  \r\n“Arwah Afeeya Sahara binti Sahak.” – Diya \r\nDan kala sebaris nama itu dibangkitkan semula, satu persatu kisah yang membawa penyesalan seumur hidup seorang Aldrish hingga kini terlayar semula di ruang fikirannya. \r\n“Jadi selama ni, you terima kehadiran I dalam hidup you, you sudi berkahwin dengan I, bukan atas sebab you cintakan I ke? Bahagia kita selama ni, you berpura-pura? Sepanjang kita bermula, you sengaja perbodohkan I dengan lakonan you? Macam tu ke Audiya Sofea? Tell me!” \r\nBerkecai sebuah cinta bila rahsia yang disimpan kejap Audiya Sofea kini bukan lagi rahsia. \r\nSetelah semua terbongkar, Diya pergi membawa diri. Tinggal Aldrish bersama kisah serbu sesal yang tidak pernah lenyap. \r\nPemergian Diya membuatkan hidup Aldrish serba tak kena seakan hilang arah. Siang malam dia terbeban dek perasaan rindu terhadap Diya. Hati beria mahu menjejak Diya, namun akal entah kenapa tidak selari dengan kehendak hatinya.  \r\nBerjayakah seorang Hud Aldrish menjejak Audiya Sofea? \r\nDapatkah Aldrish pertahankan rumahtangganya atau akur membiarkan rumahtangga yang terbina atas pendustaan itu kecundang? \r\nDapatkah Aldrish mengembalikan bahagia yang pernah dikecapinya sebelum ini sekaligus memadamkan sengketa dendam lama itu? \r\nApa sebenarnya kaitan Afeeya Sahara dengan Aldrish hingga kisahnya membawa kesan besar pada rumahtangga Aldrish dan Diya?“I tahu I bersalah. I menyesal. Sumpah penyesalan tu sampai sekarang membelenggu I. Satu benda yang you perlu tahu Diya, cinta I dekat you bukan dusta.', '../ebooksimage/687d4c15dd0ce_DIYA ALDRISH - PENGHUJUNG DENDAM.jpg', 275, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(13, 13, 'Raisya Azliey', 'HATI YANG TERPILIH', 'Hanya kerana kisah silam ibu bapa, badai bertimpa-timpa menguji percintaan dua jiwa, Adawiyah dan Isyraff.\r\nKisah cinta mereka yang bermula sejak masing-masing masih di alam persekolahan tidak pernah sekali pun mendapat restu daripada ibu Adawiyah dan ibu Isyraff sendiri. Masing-masing berkeras menolak pilihan hati anak-anak. \r\nNamun kerana hati yang masih kental dan teguh menggenggam cinta itu, mereka bertahan hinggalah masing-masing melangkah ke alam pekerjaan.\r\nCuma nasib Adawiyah tidak sebaik Isyraff. Kalau Isyraff dengan kerjayanya di awan biru sebagai pilot, dikelilingi gadis-gadis cantik, rezeki Adawiyah pula sebagai operator di kilang tidak jauh dari rumahnya. \r\nTernyata mencipta mimpi menjadi satu kisah reality yang  membahagiakan bukan perkara mudah. Tidak cukup dengan tentangan ibu masing-masing dalam hubungan mereka, percintaan mereka terus diuji apabila tiba-tiba tersebarnya berita mengenai Isyraff bakal berkahwin dengan seorang pramugari cantik jelita, Bella, yang merupakan anak gadis seorang jutawan terkenal. Dan lebih menguatkan isu itu apabila terus tersebar kisah lelaki itu dan Bella sedang hangat bercinta.\r\nPerit menghadam kenyataan itu namun Adawiyah tidak cepat melatah. Selagi tiada penjelasan dari bibir lelaki itu, tidak mungkin dia telan bulat-bulat semuanya. \r\nDemi percintaan yang sudah bertahun lamanya terjalin, Adawiyah tetap kebalkan hati menghadap penghinaan demi penghinaan Adibah, ibu Isyraff yang tak pernah berhenti mahu memisahkan mereka. \r\nNamun, kemuncak ujian percintaan mereka apabila Adawiyah tanpa sengaja menyaksikan sendiri Isyraff berdua-duaan dengan Bella di rumah sewa Isyraff sendiri. Lalu hati yang bertahun lamanya kental dan kebal akhirnya lebur. \r\nTiada lagi ruang kemaafan buat lelaki yang sekian lama menjadi penghuni hatinya itu, Adawiyah tekad memilih berkahwin dengan Mukhlis, kawan baik sendiri yang sudah lama menjadi pilihan ibunya Shuhadah. \r\nBiar berkali-kali dipujuk, Adawiyah yang ini bukan lagi Adawiyah yang sebelumnya yang tegar membiarkan setiap ceruk hatinya dipenuhi gores-gores luka hanya kerana mahu mempertahankan percintaan yang dulu. Bagi Adawiyah pengkhianat cinta seperti Isyraff tidak layak dimaafkan.\r\nSayangnya di saat hampir bahagia itu menjadi milik seorang Adawiyah, sekali lagi kiriman takdir Tuhan hadir menyesakkan dadanya, buatkan air matanya tumpah lagi.\r\nMasih adakah sisa bahagia yang tertinggal untuk Adawiyah kecapi? \r\nSeandainya benar bagai dianggap Adawiyah bahawa Isyraff itu pengkhianat cinta, saat dia tekad memilih lelaki bernama Mukhlis, kawan baik yang sentiasa ada disisinya sejak dulu, kenapa badai masih bertimpa mendekatinya? Seolah bahagia itu tidak pernah tertulis untuk dia miliki.\r\nDia, Adawiyah. Dia hanya mahu menikmati sekelumit bahagia. Terlalu mahalkah nilai sebuah kebahagiaan itu buat dirinya hinggakan begitu sekali takdir menimpanya?', '../ebooksimage/687d4c5306487_HATI YANG TERPILIH.jpg', 698, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(14, 14, 'Raisya Azliey', 'BAD LIAR', 'Apa perasaan anda seandainya mendapati kehidupan yang anda sedang lalui ketika ini hakikatnya bukanlah laluan hidup anda yang sebenarnya? Dalam erti kata lain, apa yang sedang anda lalui ketika ini semuanya dusta belaka. Dan hakikat kehidupan sebenar anda yang terbentang di hadapan mata sebenarnya adalah hakikat yang terlalu perit untuk diterima.\r\nIni cerita takdir seorang Darra Azalea. Dia terlibat dalam kemalangan langgar lari. Dia koma.\r\nBangkit dari koma, Darra Azalea hilang ingatan. Dia dibawa pulang ke Villa Qaseh. Menikmati kehidupan yang serba serbi baru. \r\nDarra Azalea merasakan segalanya begitu mengelirukan dan mengusutkan fikiran. Dia tidak dapat mengingati langsung segala kisah masa lalunya.\r\n“Ini rumah kita. Dua orang ni anak-anak kita. Yang sulung ni, Aiman, yang si adik ni Imani. Kakak dua orang ni pulak maid kita. Ini Kak Cah, dan yang ni Kak Piah.” – Azmarul\r\n“Ini anak-anak kita? Kenapa cara dia orang berdua ni pandang saya tak macam saya ibu dia orang. Dia orang macam takut je dengan saya.” – Azalea\r\n“Dia orang bukan takut tapi terkejut tu sebab masih rasa tak percaya yang awak akan balik semula ke rumah kita macam sekarang. Dekat setahun awak koma sayang. Dekat setahun budak-budak ni jalani hidup tanpa awak. Mestilah banyak benda yang berubah.” – Azmarul\r\nDarra Azalea telan saja setiap penjelasan Azmarul. Baginya untuk apa dia menambahkan rasa keliru di fikirannya lebih-lebih lagi dalam keadaan dia hilang ingatan sekarang ni. Dia hanya mahu menikmati hidup serba tenang dan bahagia.\r\nAdanya Darra Azalea di Villa Qaseh ternyata benar-benar membawa perubahan yang cukup besar. Dalam tak sedar, dia mengembalikan semula sinar bahagia yang pernah wujud di Villa itu suatu ketika dulu. Lebih terkesan lagi, senyum yang pernah memudar sekian lama pada bibir seorang Azmarul kini kembali semula.\r\nAzmarul benar-benar bahagia dengan adanya Azalea di sisi. Tidak pernah dia menduga, bahagia yang dia sangka tidak akan muncul lagi menyinari hidupnya beberapa tahun yang lalu kini hadir buat sekian kalinya. Hingga dia hampir lupa akan sesuatu.\r\n“Tuan, sampai bila semua ni? Tuan tak boleh lama-lama begini. Berdosa besar.” – Kak Cah\r\n“Tapi saya tak ada pun buat benda tak senonoh dengan Azalea.” -  Azmarul\r\n“Hukum tetap hukum, Tuan. Tuan kena cari jalan perbetulkan apa yang tak betul ni, Tuan. Tolonglah.” – Kak Cah\r\nAzmarul terhimpit. Antara bahagia yang kini kembali menyinari hidupnya dan anak-anak. Dan hakikat yang perlu dia akur biarpun perit.\r\nKeadaan semakin bertambah kusut bila Azalea mula menyimpan syak.\r\n“Kenapa kita tak pernah bersama semenjak saya balik ke Villa ni? Kita macam bukan pasangan suami isteri. Tiap kali saya ajak, ada je alasan abang mengelak. Sejak saya balik ke Villa ni, dah hampir enam bulan abang. Kita sekali pun tak pernah bersama. Waktu tidur, mesti abang akan suruh saya tidur dulu. Bila saya bangun pagi, abang tidur dekat sofa. Takde pun sebelah saya. Ada yang tak kena ke dengan saya ni, abang? Sampai abang macam jijik nak sentuh saya? Kita memang macam ni ke sebelum ni?”\r\nTerasa berdosanya Azmarul tatkala soalan bertubi-tubi begitu meluncur dari bibir Azalea. Jujur, sejak dia bawa pulang Azalea ke Villa Qaseh, hatinya makin kuat menyayangi dan mencintai Azalea.\r\nKerana tidak sanggup kehilangan, dia bulatkan tekad mengupah penyiasat untuk siasat latar belakang seorang Darra Azalea yang dia bawa menjadi ‘mommy ganti’ anak-anaknya. Niatnya cuma satu. Dia nak betulkan semula semua ini dan dia mahu Azalea menjadi haknya yang halal.\r\nMalangnya hasil keputusan siasatan memaksa Azmarul menguburkan impiannya yang satu itu. Cinta yang berputik dihati melebur.\r\n“Darra Azalea Tan Seri Adnan. Perempuan kau simpan dekat rumah kau ni isteri orang, bro.” – Mustaqim\r\nBuat sekian kali Azmarul terpaksa akur bahawa takdir bahagia itu bukan ketentuannya. Lalu jalan paling terbaik dia perbetulkan semula apa yang sepatutnya dia lakukan dari awal lagi.\r\n“Mencintai tidak semestinya memiliki, Azalea. Pulanglah ke sana. Kepada mereka yang lebih memerlukan.” – Azmarul', '../ebooksimage/687d4c95995de_BAD LIAR.jpg', 332, 0, 16.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL);
INSERT INTO `ebooks` (`id`, `no`, `penulis`, `tajuk`, `description`, `file_path`, `muka_surat`, `perkataan`, `harga_rm`, `genre`, `bulan`, `tahun`, `penerbit`, `created_at`, `uploaded_by`) VALUES
(15, 15, 'Raisya Azliey', 'ISTERI TAK DIANGGAP', 'BERTEMU JODOH dan berkahwin dengan lelaki yang kita cintai serta mencintai diri kita, bahagia rasanya kan?\r\nTakdir seorang Rindu Ateefa mempertemukan dirinya dengan Firaas Andika - seorang businessman handsome berhati beku. Seorang lelaki yang menjadi kegilaan kaum perempuan dan Rindu Ateefa salah seorang yang meminatinya.\r\nFiraas Andika lelaki paling sukar didekati kaum perempuan. Bukan tiada yang cuba menakluki hati lelaki itu. Ramai yang mengejar dan mahu menakluki. Semuanya tidak dipandang walau sebelah mata. Dan yes, satu ciri-ciri Firas Andika yang itulah buat Rindu Ateefa makin terpaut cinta. Dia yakin lelaki ini pencinta yang setia.\r\nBagi Rindu Ateefa, lelaki itulah Mr Rightnya. Lelaki yang diharap akan sentiasa berada di sampingnya, mencintai setulus hati dan mencipta bahagia bersamanya sampai mati. \r\nSayangnya si Mr Right tiba-tiba berubah sikap usai berubahnya status mereka menjadi suami isteri. \r\nBelum pun sempat merasa nikmatnya bergelar isteri kepada Firas Andika, mahligai bahagia yang diharap-harap sudah dirasakan umpama neraka dunia buat Rindu Ateefa.\r\n\"Heh, siapa suruh you bawak luggage you naik atas? I ada suruh ke you naik atas?\"\r\n\"Tadi you cakap bilik you kat atas kan?\"\r\n\"Yes, bilik I. Bukan you. Tempat you nak tidur, bukan di bilik I, Teef. You tidur dekat bilik Kak Kiah. You hanya dibenarkan bermalam di bilik I time I nak \'pakai\' you je.\"\r\n\'Kejutan\' tak dijangka dari sang suami tercinta umpama halilintar yang memanah sekeping hati. Jantung Rindu Ateefa seakan direntap melihat perubahan sekelip mata lelaki itu.\r\n\"Kenapa you berubah sikap lepas kita kahwin, Dika? Mana pergi Firas Andika yang sweet selalu tu? Yang romantic, yang selalu buat jiwa I terbuai. You yang depan I sekarang ni macam bukan Firas Andika yang I kenal.\" \r\nMana Rindu Ateefa tak pelik dengan perubahan mendadak lelaki itu. Sebelum ini semuanya nampak sempurna pada diri Firas Andika. Malah mereka tidak pernah bersengketa.\r\n\"Kita baru je kahwin. Belum seminggu pun. I ada buat salah apa-apa ke dekat you? Kalau ada please tell me, Dika. I cuba ubah apa yang tak kena. Untuk kebaikan kita.\"\r\nMengenali Firas Andika, dia hanya kenal sisi baik semuanya mengenai lelaki ini. Tidak pernah dia jangka semua ini akan dia hadap.\r\n\"Malam ni I nak you bersiap cantik-cantik, wangi-wangi, tepat jam 10 masuk bilik I. I nak \'pakai\' you. Pastikan jangan ada bau ikan masin. I boleh muntah nanti.\"\r\nRindu Ateefa sabar mulanya. Dituruti segala rentak tingkah sang suami walau hati seakan dilapah rasanya setiap kali Firas Andika menyakiti. \r\n\"You buat I tak ubah macam pelacur, Dika. You buat I macam takde perasaan.\"\r\n\"Itu belum separuh pun penderitaan yang you rasa nak bandingkan penderitaan yang I rasa sejak zaman I kanak-kanak. Bukan ke kita ni berkongsi hidup? Apa gunanya you jadi wife I kalau you tak dapat berkongsi derita yang I pernah rasa?\"  \r\nIkutkan rasa hati, Rindu Ateefa mahu membawa diri keluar sahaja dari rumah Firas Andika. Andai beginilah taruhan takdir sesudah bergelar isteri kepada Firas Andika, dia rela berundur walau hati masih menyebut kata cinta buat sang suami.\r\nNamun gerak hati yang berlainan bicara memaksa Rindu Ateefa menggali punca Firas Andika berubah sikap terhadapnya. Dia yakin ada sesuatu yang perlu dia tahu dari lelaki itu.\r\nDan kala rahsia yang satu itu Firas Andika bongkar satu persatu, Rindu Ateefa terjelepuk ke lantai. Lemah menghadam kenyataan di depan mata. Marah, sakit hati, semuanya bergumpal dalam dada Rindu Ateefa. \r\n\"So I ni dikira perempuan terpilih lah ye, Mr. Firaas Andika? Perempuan terpilih untuk you humban segala dendam yang you heret sepanjang hidup you selama ni? Hey, Dika... bukan semua perempuan tu sama. You tak boleh letakkan hukuman macam ni untuk semua perempuan atas muka bumi ni. Tak adil, Dika!\"\r\n\"Hukuman ni juga sebagai balasan apa yang orang tua tu dah buat dekat family I.\"\r\nSaat ini harapan-harapan yang pernah wujud direlung hati Rindu Ateefa seakan berguguran semuanya. Rindu Ateefa bawa diri kerana sedar tiada guna meneruskan rumahtangga bersama lelaki yang mengagungkan dendam dihati. Dia mencintai, namun lelaki itu sebaliknya. \r\nBenarkah telahan Rindu Ateefa bahawa semuanya selama ini hanya bersandarkan dendam? \r\nJika benar kerana dendam, mengapa saat Rindu Ateefa sudah menjauh, hati seorang Firas Andika mula merasa sakit dan merindu? Pejam mata, lelaki itu nampak bayangan si manis Rindu Ateefa.  \r\nMalahan mengenang tangisan Rindu Ateefa, sakit dalam dadanya tidak terperi rasa.', '../ebooksimage/687d4cd6bcbf3_ISTERI TAK DIANGGAP.jpg', 269, 0, 16.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(16, 16, 'Raisya Azliey', 'TUAN FARITH : SKANDAL TAK DIRANCANG', 'Demi mengelak dari dipaksa kahwin, Faizura nekad \'pinjam\' Tuan Farith, bos dia sendiri untuk diperkenalkan kepada mak di kampung sebagai calon suami.\r\nItu pun setelah habis semua makhluk bernama lelaki yang Faizura kenal, semuanya menolak permintaan Faizura untuk dijadikan boyfriend sewaannya. Mujur calon terakhirnya ini bersetuju. \r\n\"Kalau Elis tahu ni, mati saya karang.\"\r\nFAIZURA yang tak pernah mempunyai teman lelaki terpaksa berhadapan dengan masalah besar bila keluarganya sepakat mahu menyatukan dia dengan sepupu sendiri yang baru tamat pengajian dari Mesir.\r\n\"Hal ni antara kita je, Tuan. Kalau Tuan tak buka mulut, saya tak buka mulut, siapa pun tak tahu hal ni.\"\r\napabila Faizura yang bawa pulang Tuan Farith diperkenalkan kepada keluarganya dikampung,mereka dikejutkan dengan majlis pernikahan yang sedang diatur keluarganya.\r\n\"Apa ni mak? Mak cakap nak bincang? Takde pulak bagitahu nak buat majlis nikah terus? Fai tak nak nikah dengan abang sepupu sendiri, mak. Fai dah ada boyfriend. Fai nak kahwin dengan dia.\"\r\nMak bagi kata dua, \"Pilihan kau, kahwin dengan abang sepupu kau atau dengan boyfriend kau. Tu je Fai.\"\r\nPening kepala Faizura. Lagi pening kepala si boyfriend sewaannya.\r\n\"Awak cakap sewa jadi boyfriend je. Ni memang nak bagi Elis bvnuh saya ni.\"\r\nTengok riak susah hati si Faizura, dia pun susah hati sama. Sudahnya dia putuskan turuti permintaan luar jangka si Faizura. \r\n\"Jadi isteri saya, awak takde keluarga mentua sebab of course la family saya tak akan tahu status awak sebagai isteri saya.\"\r\nBergelar suami isteri rahsia, tidak semudah yang disangka. Tambahan lagi mereka berkerja dibawah satu bumbung. Dalam diam cemburu mula timbul dihati masing-masing.\r\nTanpa mereka sedar, hati mereka dipesan-pesan agar tidak terlanjur rasa kini menderhaka.\r\n\"Tuan boleh tak kalau depan mata saya jangan mengg4-tal sangat dengan Elis? Tuan selamba je biar si Elis tu p3luk Tuan, cubit-cubit pipi tuan. Main-mainkan rambut tuan. Tuan belum halal lagi dengan dia. Buat cara belum halal.\"\r\nBila ditangkap terang-terang begitu, Faizura kehilangan kata. Gil4 dia nak buat pengakuan mudah-mudah.\r\n\"Fai, ni first warning yea. Saya tak nak awak keluar makan dengan berdua dengan si sotong tu lagi. Kalau saya nampak, lunyai sotong tu saya masak!\"\r\n\"Apa tuan ni nak marah-marah Tod macam tu? Kami pergi makan berdua sebelum ni elok je, tuan tak kacau pun. Sotong-sotong Tod tu pun, dia bukan buat benda tak elok dekat saya. Tuan datang bulan ke pelik je perangai tu.\"\r\nHmm... Tuan Farith pun dua kali lima dah!\r\nSebelah Puan isteri, sebelah cik tunang. \r\nFarith buntu. Hati dah menderhaka. Tapi ada janji yang perlu dia buat pada tunang sendiri.\r\nFaizura perlu mengalah.. dari awal lagi dia yang buat rancangan. Berkahwin hanya untuk berpisah?', '../ebooksimage/687d4d2801c70_TUAN FARITH - SKANDAL TAK DIRANCANG.jpg', 300, 0, 8.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(17, 17, 'Raisya Azliey', 'LAURA ARDEL', 'Memegang status bujang di usia mencecah 39 tahun, Rayyan Anaqi menyerahkan seratus peratus urusan jodohnya ditangan mama dan papanya yang menentukan siapa calon isterinya. \r\nTakdir ternyata menyatukan dia dengan si jelita Laura Ardel yang merupakan pelajar paling kerap menimbulkan masalah kepadanya di universiti tempat dia mengajar.\r\nPening kepala Rayyan nak hadap Laura 24 jam. Sudahlah di universiti pun tak habis-habis kisah Laura buat hal dekat dia jadi bahan cerita. Balik di rumah, dia kena layankan lagi perangai tak matang Laura.\r\n\"Kenapa you tak tolak je cadangan family kita masa tu?\" - Rayyan\r\n\"Entahlah. Maybe sebab Lee Min Ho.\" - Laura \r\n\"Mama tunjuk gambar calon suami. Lelaki dalam gambar tu ada iras wajah pelakon korea yang I minat gila. Mestilah I setuju. Mana la I tahu manusia dalam gambar yang mama tunjuk adalah orang yang sama dengan pensyarah paling I menyampah dekat universiti tu.\"\r\n\"Lagi pun dalam gambar mama tunjuk tu orangnya nampak muda. Muka licin flawless je. Serius nampak ada iras Lee Min Ho. Takde banyak semak samun macam sekarang ni.\" - Laura\r\n\"So you akuilah yang I memang kategori lelaki hot dan handsome? You selalu buat hal dekat I selama ni sebab nak perhatian kan?\" - Rayyan\r\n\"Sorry sikit. Pakcik jangan nak perasan lebih. Manusia dalam gambar mama tunjuk tu dengan reality yang ada depan mata I sekarang ni lain. Macam langit dengan bumi. I ni kira mangsa penipuan, tahu. Kalau bukan sebab kasihankan mommy dengan mama, hari majlis akad nikah tu, memang I dah lari dah. And one more, I tak pernah tergilakan perhatian you, pakcik tua.\" - Laura\r\nYa, Itu kata Laura. Ucapan yang sekadar menjadi mainan dibibir. Bukan kata hati.\r\nPerang mulut Rayyan dengan Laura, tak pernah menemui noktah. Sepatah Rayyan tanya, berpuluh patah Laura balas. Segala urusan rumahtangga, semuanya terbeban di bahu Rayyan seorang.\r\n\"Laura Ardel, berapa kali I nak kena ingatkan, pakaian yang you dah pakai masukkan elok-elok dalam bakul sebelah mesin basuh tu supaya senang I nak cuci?  Ni satu lagi... seluar kecik you setiap kali I nak cuci, semua bergulung-gulung. Dah menambah kerja I pulak nak buka-buka. Kalau wangi tu lain cerita.\" \r\nBeristerikan perempuan tak berapa matang, hidup seorang Rayyan berubah sepenuhnya. Sakit hati dek tingkah menduga si Laura tak pernah habis. Sampai satu masa, Rayyan mula rasa perkahwinannya dengan Laura satu beban.\r\n\"Orang kahwin nak bahagia, ma.  Nak dilayan isteri elok-elok. Rayyan ni kahwin dengan budak tak cukup umur tu, jangankan nak rasa bahagia, rasa nak pecah kepala Rayyan ni ada la. Dekat universiti mengadap dia, balik rumah mengadap dia. Serabut, ma. Serabut!\" - Rayyan\r\nBagi Rayyan, daripada meneruskan perkahwinan yang buat kepala dia serabut  sampai rasa nak pecah tu, lebih baik bercerai sahaja.\r\n\"Sabar la sikit, Rayyan. Mama dengan papa ni nampak Rayyan je sesuai dengan Laura. Rayyan je boleh ubah sikap Laura tu. Mama kesian Aunty Syaz. Menangis-nangis dia cerita pasal perangai  Laura semenjak tahu status sebenar dia. Aunty Syaz risau apa-apa yang tak elok jadi dekat Laura nanti. Itu yang dia nak kahwinkan Laura. Dan dia nampak Rayyan baik orangnya. Dia percaya Rayyan yang boleh jaga Laura.\" - Datin Rahimah \r\nHubungan Rayyan dan Laura tidak seperti pasangan suami isteri yang lain. Mereka seperti orang asing walaupun tinggal sebumbung, berkongsi katil dan selimut yang sama. Bahkan walau usia perkahwinan hampir mencecah usia satu tahun, Rayyan masih belum menyentuh Laura selayaknya. \r\nNamun segalanya mulai berubah bila Laura dapat tahu pensyarah perempuan yang baru masuk mengajar di universitinya adalah ex girlfriend Rayyan dan masih ada hati terhadap Rayyan.\r\n\"Ni kenapa pakai baju tak cukup kain macam ni? Mana pergi baju kaftan yang berlambak-lambak you pakai sebelum ni?\" - Rayyan\r\n\"Orang kata suami selalunya suka isteri dia seksi depan dia. Pakcik suka tak I seksi macam ni depan pakcik? Kalau pakcik suka, lebih dari ni pun I boleh tunjuk tau. Pakcik nak?\" - Laura\r\nBila Laura berubah nakal begitu, Rayyan mula jadi tak keruan. Telan liur pun rasa tak lepas. Sesak nafas dibuatnya.\r\nDemi memikat dan mengikat hati encik suami, Laura buat perubahan besar! Semuanya kerana satu misi. Ikat mati hati Rayyan dengan hatinya. \r\nAgak-agak berjaya tak Laura Ardel ikat mati hatinya dengan hati pakcik  hot si Rayyan Anaqi tu?\r\nRayyan pula, antara Laura yang halal disisinya dan ex girlfriend yang kembali menagih cinta lama, mana satu dia akan pilih? Laura yang tak berapa nak matang tu ke atau ex girlfriend yang cukup pakej untuk dia buat bini sekarang ni?', '../ebooksimage/687d4d63db733_LAURA ARDEL.jpg', 310, 0, 15.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(18, 18, 'Raisya Azliey', 'DEEJA FAHRY : BAHAGIA YANG KAU TUNDA', 'Menyambung pengajian di Luar Negara, takdir menyatukan jodoh Fahry dan Deeja. Kata Fahry, perkahwinan mereka mendapat restu keluarga. Di pihak Deeja pula, seluruh ahli keluarga Deeja menyambut baik Fahry sebagai suami Deeja. Perkahwinan mereka dikurniakan cahaya mata perempuan - Aurylea.\r\n\r\nTiba masa pulang ke Malaysia, terbongkar rahsia yang Fahry simpan kejap. \r\n\r\n\"Abang mintak maaf, Deeja. Abang terlalu cintakan Deeja. Abang tak sanggup hilang Deeja. Abang nak halalkan Deeja jadi hak abang yang sah. Sebab tu abang terpaksa tipu Deeja. Kalau abang terus-terang semuanya, Deeja tentu tidak akan menerima abang.\" - Fahry\r\n\r\nHancur hati Deeja hanya Tuhan yang tahu.\r\n\r\n\"Sampai hati abang tipu Deeja. Abang memang kejam!\" - Deeja\r\n\r\n\"Kalau abang ceritakan semuanya, boleh ke Deeja terima abang? Abang tak pernah cintakan mana-mana perempuan seperti mana apa yang abang rasa terhadap Lis Khadeeja.\" - Fahry\r\n\r\nFahry cakap dia tak boleh kehilangan Deeja kerana Deeja cinta matinya. Tapi tahukah Fahry, sebaik sahaja mereka menjejak bumi Malaysia takdir sudah pun menjarakkan cinta yang terpasak antara mereka.\r\n\r\n\"Beri abang masa untuk abang selesaikan pasal kita.\" - Fahry\r\n\r\nFahry minta Deeja bersabar. Fahry minta Deeja bertenang. Fahry minta Deeja tunggu hingga segalanya selesai.\r\n\r\nMasa terus berlalu. Dan Fahry yang meminta Deeja menunggu tiba-tiba sahaja lenyap tanpa khabar.\r\n\r\n\"Kenapa papa dah lama tak balik sini, mama? Lea rindu, papa. Papa tu dah tak sayang kita kut?\" - Aurylea \r\n\r\nLantas demi bahagia yang tertunda, Deeja cari jalan menjejak sang suami. Pencariannya menemui noktah bila melihat depan mata sendiri Fahry yang dia jejak kini bukan lagi Fahry yang dulu.\r\n\r\nKenyataan yang terhidang depan mata buatkan jiwa seorang Lis Khadeeja remuk. Seksa perasaannya. Terasa mahu saja bawa diri keluar dari hidup lelaki itu namun tegakah dia berlaku kejam kala lelaki itu dalam keadaan yang tak pernah dia jangkakan?\r\n\r\nBagaimanakah kesudahan kisah rumahtangga Deeja dan Fahry? Dapatkah Deeja menerima hakikat yang terhidang di depan mata? Masihkah ada sisa bahagia yang dinanti Deeja dari Fahrynya?', '../ebooksimage/687d4da5d54d2_DEEJA FAHRY - BAHAGIA YANG KAU TUNDA.jpg', 253, 0, 15.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(19, 19, 'Raisya Azliey', 'UNTUK 5 HARI KAU DAN AKU', 'Dinda dan Zaqrul. Mereka pasangan yang mendirikan rumahtangga di usia muda. Selepas SPM. Perkahwinan atas restu keluarga kedua belah pihak membawa kebahagiaan buat mereka. \r\nNamun takdir menguji apabila mereka belum dikurniakan zuriat walaupun usia perkahwinan telah memakan masa bertahun-tahun. Kedua-duanya telah berusaha dengan pelbagai cara. Menurut doktor juga, mereka tiada sebarang masalah.\r\n\"Mungkin Allah mahu kita bercinta dulu. Abang okay je, sayang. Abang tetap bahagia.\"\r\nItu kata Zaqrul. Tapi betul ke dia bahagia?  Atau itu hanya omongan buat legakan hati Dinda?\r\nBagi Dinda, benda paling buat dia kecewa yang inilah. Dia gagal berikan zuriat buat suaminya Zaqrul. Isteri mana yang tidak kecewa. Isteri mana yang tidak merasa tercabar. Melihat isteri orang lain, beranak pinak. Dia, seorang pun belum.\r\nSuatu hari Dinda menerima kejutan.\r\n\"Abang nak minta izin kahwin lain. Perempuan tu, staff baru tempat abang kerja. Janda anak dua.\"\r\nSatu permintaan yang buat hati Dinda terasa dikelar-kelar. Allahuakbar! Sakitnya. Namun untuk menghalang keinginan Zaqrul, dia sedar dia tidak layak. Zaqrul berhak kerana Zaqrul mampu. Malah Dinda yakin perempuan kedua itu pastinya mampu memberikan apa yang tidak pernah Dinda mampu beri pada Zaqrul sepanjang perkahwinan mereka. \r\n\"Dinda izinkan abang kahwin lagi. Cuma satu Dinda nak mintak. Dua minggu sebelum abang kahwin, boleh tak abang luangkan masa abang untuk Dinda sepenuhnya tanpa sebarang gangguan. Hanya untuk 5 hari sahaja.\"\r\nItu permintaan Dinda. Melakar kisah 5 hari paling indah sebelum berkongsi kasih suami dengan perempuan lain. \r\nBagi Dinda, 5 hari itu dia akan pastikan setiap detik mereka lalui penuh kemanisan dan keindahan. Biar kenangan bersama Zaqrul itu kekal menemaninya saat Zaqrul tiada disisi.\r\nZaqrul akur. Menurut dengan rela asalkan hajat dihati dapat dilaksanakan. Tambah menguatkan keinginan Zaqrul bila sahabat dan keluarganya sendiri menyokong keputusannya.\r\nMalangnya usai tamat permintaan 5 hari Dinda, lain pula perasaan yang membelenggu Zaqrul. \r\nApakah kesudahannya selepas 5 hari itu yang dilalui Dinda dan Zaqrul?', '../ebooksimage/687d4deacc32c_UNTUK 5 HARI KAU DAN AKU.jpg', 307, 0, 19.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(20, 20, 'Raisya Azliey', 'TUAN PENJAGA CIK KESAYANGAN', 'Semua yang berlaku diluar kemahuan Haura sendiri. \r\nHaura bercinta dengan Fattah. Bukan sebulan dua usia percintaan mereka. Sudah tentunya dia mengharapkan hanya lelaki bernama Fattah itu yang akan bergelar suaminya. \r\nNamun siapa sangka, Fattah punya permintaan luar jangka yang tak pernah Haura fikir. \r\n\"Awak buat macam ni, awak ingat cinta kita ni barang yang boleh dijual beli ke apa? Awak sedar tak permintaan gila awak ni boleh makan diri sendiri?\"\r\n\"Saya percaya pada Zaem, Haura. Sampai masa yang tepat, dia akan serahkan awak pada saya dan kita akan berbahagia bersama.\"\r\n\"Jangan menyesal dengan keputusan gila awak ni, Fattah.\"\r\nFattah - Dia seorang pencinta yang setia. Cinta dan kesetiaannya hanya untuk Haura seorang. Malangnya dia cuma budak kampung yang bekerja sebagai pemandu lori kedai perabot tidak jauh dari rumahnya. Sedangkan Haura pula anak orang berada. \r\n\"Babah suruh awak pinang saya dalam tempoh tiga bulan lagi. Babah tak mintak banyak pun. Tiga puluh ribu je. Kalau awak tak masuk meminang saya, babah cakap nak kahwinkan saya dengan orang lain.\"\r\nBila keluarga Haura mendesak dia memperisterikan Haura segera dalam keadaan kewangannya yang masih belum  stabil, tiada pilihan lain, dia terpaksa melibatkan sahabat baiknya Zaem Khalif. \r\nDengan harapan...\r\n\"Aku percaya kau sorang je, Zaem. Aku sayang sangat Haura. Aku memang nak kahwin dengan dia. Aku takut lagi lama Haura tunggu aku, aku akan hilang dia. Daripada lelaki lain pinang dia, baiklah kau je Zaem. Kau pinang dia. Tolong aku, tolong jadi tuan penjaga Haura aku tu, versi halal. Dalam lima tahun je, Zaem. Cukup modal aku kumpul, kau lepaskan Haura.\"\r\nLima tahun Fattah mintak masa. Lima tahun Haura dalam jagaan Zaem. Lima tahun yang bakal mengubah segalanya.\r\nHidup sebumbung, berkongsi bilik, berkongsi katil. Dari tak biasa, Haura perlahan-lahan menerima kewujudan Zaem sebagai suaminya. \r\nZaem yang soft spoken, Zaem yang pandai ambil hati, Zaem yang sangat sweet dan caring. Haura mulai jatuh cinta pada suami sendiri.\r\nSampai satu masa, Haura sedar  ruang di hatinya sudah terisi lelaki yang kini bergelar tuan penjaga halalnya. Zaem Khalif.\r\n\"Tengok orang lain kahwin, ada anak. Best je. Kita berdua takde. I sunyi. I rasa, I dah ready untuk jadi mommy kepada anak-anak you, Zaem.\"\r\n\"What? You lupa perjanjian I dengan Fattah macam mana? Cukup lima tahun, Fattah akan kembali menagih janji tu. I tak  dituduh...\"\r\nTak sempat Zaem noktahkan ucapan, Haura pangkas dengan kucupan pertama pada bibir lelaki itu. Kucupan yang buat Zaem hilang diksi meneruskan apa yang mahu dilontar dari bibirnya.\r\n\"I tahu you cintakan I, Zaem. Cuma perasaan tu you pendam sebab best friend you, kan?\"\r\nHaura yang bijak menyelongkar isi hati Zaem, buatkan segalanya bukan lagi rahsia. Bila Haura beraksi manja dan menggoda, Zaem akhirnya menyerah. \r\nBagi Haura, perkahwinan mereka adalah satu takdir. Mereka bergelar suami isteri kerana jodoh aturan Dia Yang Maha Kuasa. \r\n\"Janji dengan I, Zaem. Apa sekali pun yang akan berlaku, you tak akan tinggalkan I, you tak akan buang I.\"\r\nDengan Haura Zaem berjanji. Dengan Fattah pun Zaem berjanji.\r\nSaat Zaem dan Haura di puncak bahagia, Fattah kembali menagih janji. Meminta Zaem melepaskan Haura kepadanya. \r\nLantas hati seorang suami yang telah sarat dengan cinta buat isterinya mula dilema. Antara janji dengan seorang sahabat, dan janji dengan isteri.\r\n\"Aku dah bersedia, Zaem. Aku nak Haura aku semula.\"\r\n\"I tak boleh hilang you, Zaem. Jangan tinggkan I. I cintakan you. I dah mengandung. Dalam rahim I, ada anak kita.\"\r\nAntara Fattah, Zaem dan Haura, siapakah yang akan berundur? Semuanya punya satu keinginan yang sama. Bahagia.', '../ebooksimage/687d4e2fbdf90_TUAN PENJAGA CIK KESAYANGAN.png', 292, 0, 20.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(21, 21, 'Raisya Azliey', 'YANG PERNAH PERGI', 'TYARA dan SYAFF pernah bergelar suami isteri. Ikatan perkahwinan atas dasar cinta itu mendapat restu kedua belah pihak keluarga pada mulanya. Namun bahagia yang diharap berkekalan hanya mampu bertahan separuh jalan. Kerana kesilapan diri sendiri, ikatan jodoh antara mereka terlerai dikala usia perkahwinan baru memasuki tahun ke dua. \r\nBerpisah. Masing-masing bawa haluan sendiri dengan janji tidak akan saling muncul antara satu sama lain. \r\nBertahun-tahun berlalu, Tyara cuba membuka pintu hati untuk insan lain bertahta. Siapa sangka hati masih merindu yang telah pergi. Dan takdir rupanya masih menyimpan satu lagi pertemuan buat mereka berdua. \r\nKetika pandangan mereka bersatu, masing-masing terkejut.\r\n\"Syaff? Syaffeq Matten?\" \r\n\"Tyara Jasmin?\"\r\nTernyata episod pertemuan semula itu membangkitkan semula satu rasa yang suatu ketika dulu mekar dihati masing-masing. Lebih-lebih lagi apabila mendapat tahu kedua-duanya belum dimiliki insan lain.\r\nSyaff yang masih memahat kukuh Tyara di hatinya sejak dulu dan Tyara yang tak pernah sekalipun berjaya singkirkan Syaff dari hatinya.\r\nKerana cinta yang masih utuh, mereka putuskan untuk menyambung bahagia yang dulu terlerai di tengah jalan. Berkahwin buat kali kedua. \r\nNamun apa yang diinginkan ternyata tidak mudah kali ini. Bahagia yang didamba tidak semudah yang diharap. Puan Syarifah tidak lagi merestui perkahwinan kali kedua anaknya Syaff dengan Tyara. \r\n\"Mak belum lupa apa yang perempuan ni buat pada kau dulu,Syaff. Kau hampir jadi gila. Mak ni menangis siang malam disebabkan keadaan kau.\" - Puan Syarifah.\r\nKerana dalamnya cinta Syaff pada Tyara, Syaff tetap berkahwin dengan Tyara semula walau sedar begitu sukar Puan Syarifah menerima kehadiran Tyara kali ini.\r\nKemuncak ujian rumahtangga mereka apabila muncul Imani yang menyimpan perasaan terhadap Syaff sekian lama.\r\n\"Syaff, sampai hati you kan! You tahu I dah lama cintakan you. I dah lama tunggu you buka hati you untuk I. You sikit pun tak bagi peluang dekat I. Kalau Mak Long Pah tak cakap dekat I pasal you kahwin dengan janda you balik, sumpah I tak tahu.\" - Imani\r\n\"Dulu lagi I dah cakap dengan you, Imani. Hati I ada Tyara Jasmin sahaja.\" - Syaff\r\n\"Syaff ni, Mak Long sendiri pun tak tahu apa dah jadi dengan dia. Kaca dengan permata pun dia tak reti beza sekarang. Ingatkan sekali tersalah pilih kaca dulu, dia dah serik. Hmm, ulang lagi pilih yang sama.\" - Puan Syarifah\r\nPuan Syarifah yang keras hatinya untuk menerima kehadiran Tyara buat kali kedua. Imani yang tak pernah berhenti menginginkan Syaff. \r\n\"I memang dah gila. I gila sebab you, Syaffeq Mattin. I nak you, kenapa susah sangat you nak terima I? You tahu kan dah lama I tunggu you.\" - Imani\r\nSampai satu tahap, Tyara mula lelah dengan ujian rumahtangga yang bertimpa-timpa. Dia cintakan Syaff dengan sepenuh hati, namun keadaan seolah-olah tidak berpihak pada cintanya.\r\n\"Bagi je lah Imani tu peluang. You kahwin dengan dia.\" - Tyara. \r\nHati Tyara seakan ditoreh-toreh. Rasanya perit. Tyara bukan kuat mana. Namun siapa dia untuk melawan segala datang. Kalau dengan memberi izin Syaff menikahi Imani dapat sejukkan hati Puan Syarifah menerimanya, dia rela berkorban.\r\n\"I tahu, you suruh I kahwin dengan Imani sebab mak dengan Imani kan?\" - Syaff\r\n\"Mak you tak nak I. Antara I dengan Imani, dia lagi nak Imani jadi wife you.\" - Tyara\r\nTyara rela mengalah kerana sedar mahalnya sebuah kebahagiaan yang dia dambakan.\r\nNamun tegakah Syaff menuruti permintaan Imani dan Puan Syarifah? \r\nSanggupkah Syaff melihat Tyara hidup dalam lara?\r\n\"Kenapa kita macam susah nak bahagia, Syaff?\" - Tyara\r\n\"Ini namanya ujian, Tya. Ujian menguji sekuat mana cinta kita berdua dapat bertahan berdepan dengan ujian begini. You ada I, Tya. Don\'t worry. Selagi I bernafas, cinta dan kasih sayang I, sentiasa mengirigi langkah you.\"', '../ebooksimage/687d4e74b3d30_YANG PERNAH PERGI.jpg', 326, 0, 16.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(22, 22, 'Raisya Azliey', 'PEREMPUAN YANG TERLUKA', 'Suatu ketika dulu, Nurha pernah jatuh cinta buat pertama kalinya terhadap lelaki bernama Raykal Isqandar Dato\' Rezlan. Pernah juga dia beranikan diri meluah rasa terhadap lelaki itu. \r\n\r\nNamun kerana jurang yang begitu ketara antara mereka, cinta Nurha bukan sahaja ditolak, bahkan dia turut dihina. Dan semenjak itu, segala perasaan yang bercambah dalam hati seorang Nurha Azalea untuk seorang lelaki bernama Raykal Isqandar dicampak jauh. \r\n\r\n7 tahun berlalu. Takdir membawa sekali lagi pertemuan antara mereka berdua. Rupanya Raykal adalah big boss tempat Nurha kerja. Terasa kecilnya dunia. Nurha sangka Raykal tak akan muncul lagi dalam hidupnya.\r\n\r\n\"Kalau kau nak tahu, itulah big boss kita. Tuan Raykal Isqandar, owner Ray\'s Cafe. Tuan Raykal ni selalu call je tanya perkembangan cafe. Yang rajin turun cafe ni abang dia, Tuan Rezarel Luthfi. Masa kau mintak kerja dekat cafe ni dulu, kalau ikut Tuan Rezarel, kau tak dapat kerja tapi Tuan Raykal yang beria suruh ambik kau selepas dia tengok resume kau.\" - Nina\r\n\r\nAkhirnya Raykal muncul depan Nurha. Paling mengejutkan bila lelaki itu membawa bersama hajat yang buatkan hati seorang Nurha bagai dilanda taufan. Satu permintaan yang membangkitkan semula rasa yang pernah Nurha campak jauh suatu ketika dulu. \r\n\r\n\"Aku nak melamar kau jadi isteri aku. Aku nak jawapan kau secepatnya. Kalau kau pakai cincin ni, bermakna kau setuju dua minggu dari sekarang, kita akan bergelar suami isteri. Kalau kau reject, bermakna kau paksa aku guna cara kasar untuk buat kau beri jawapan \'terima\' tu pada aku. Aku tak terima jawapan tidak kau.\" - Raykal.\r\n\r\nRaykal yang angkuh. Raykal yang sombong. Raykal yang tak pernah nak bermanis-manis dengan Nurha. Sejak dulu sampai sekarang, itulah Raykal yang Nurha kenal. Tidak berubah langsung. \r\n\r\nYang berubah cuma satu. Keputusan Raykal yang tiba-tiba ini. Yang buat Nurha sangsi dengan Raykal.\r\n\r\nKenapa tiba-tiba je Raykal muncul bersama permintaan itu ketika ini? 7 tahun Nurha pujuk hati untuk redha bahawa antara dia dan Raykal tidak ditakdirkan bersama. 7 tahun Nurha rawat luka di hati dan sungguh segalanya bukan mudah untuk Nurha hapuskan kisah perit itu.\r\n\r\n\"Kau dengan aku macam langit dengan bumi, Nurha Azalea. Kau tak sedar diri eh? Taraf mak bapak kau, bawah tapak kaki mak bapak aku je. Kau masih nampak kita  berdua ni sesuai bercinta ke? Sorry to say la... setakat buat stock geli-geli aku, kalau kau sudi, yang ni memang aku on. Tapi kalau kau nak aku pilih kau untuk buat bini, tunggu aku hilang akal dulu la kot.\" - Raykal\r\n\r\n\"Bermakna sekarang ni tuan dah hilang akal dah la ye? Saya ni bukan ke sesuai buat stock geli-geli je.\" - Nurha.\r\n\r\nJujurnya hati Nurha masih terkesan dengan penghinaan Raykal terhadapnya dulu. Namun entah mengapa hatinya mudah benar melebur bila Raykal membuat permintaan yang satu itu. \r\n\r\nLalu dengan sekali lafaz, Nurha Azalea sah bergelar isteri Raykal Isqandar. \r\n\r\nPada Nurha, biarpun perkahwinannya dengan Raykal berlaku dalam keadaan tergesa-gesa', '../ebooksimage/687d4f0291470_PEREMPUAN YANG TERLUKA.jpg', 469, 0, 19.00, '0', '', 0, 'Raisya Azliey Publication', '2025-07-20 19:29:41', NULL),
(23, 23, 'Zati Mohd', 'CALON ISTERI TUAN HAIDER', 'Bekerja di Kiddocare membuatkan aku tersangkut secara tak sengaja dengan Tuan Haider. Duda anak satu. Sikecil yang bernama Nia.\r\nMenjaga Nia secara tak langsung membuatkan aku rapat dengan papanya. Sampailah suatu hari aku di kenalkan sebagai calon isteri Tuan Haider dekat bekas isterinya.\r\n\"Calon isteri I.. bakal ibu Nia.”\r\nAku ingatkan main-main, rupanya memang dia tekad nak jadikan aku calon ibu buat Nia. Sikap Tuan Haider yang agak gentleman dan pesona Tuan Haider membuatkan perasaan aku sedikit goyah. \r\nHinggalah suatu pagi, ayah telefon dan bagitahu Tuan Haider masuk meminang aku. \r\nKenapa Tuan Haider masuk meminang saya? - Haneesa. \r\nAdakah dia benar-benar menyayangi aku atau hanya mahu carikan pengganti untuk ibu Nia?', '../ebooksimage/687d4f463b706_CALON ISTERI TUAN HAIDER.jpg', 202, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(24, 24, 'Zati Mohd', 'MY BAE.. TENGKU FAHD', 'Setiap kali bertembung hanya mata saja yang menyapa. Tiada senyuman terukir mahupun apa-apa perkataan.\r\nIya itu yang terjadi bila aku si Zalikha asyik bertembung dengan Tengku Fahd a.k.a Tengku Fahad the eligible bachelor yang aku tengok dekat media sosial. \r\nSampailah satu hari entah mana silapnya aku dikenalkan sebagai bakal isteri Tengku Fahd. Aku ulang bakal isteri. Gila! Tak pernah terfikir dalam kepala otak aku ni yang aku akan jadi isteri dia. Lelaki yang aku pernah cakap \"malang betul bini dia nanti.\" \r\nBukan dalam kalangan ahli keluarga aku saja yang kecoh tapi satu Malaysia termasuk lah kekasih-kekasih Tengku Fahd.\r\n \"Call off the wedding.\" - Tengku Fahd.\r\n \"Ehhh saya memang tak setuju. Lagipun Tengku Fahd bukan ada ciri-ciri lelaki idaman saya pun.\"\r\n \"Then apa ciri-ciri lelaki idaman awak?\" Keningnya terjungkit sedikit.\r\n \"Act with services, romantik, baik, jujur, setia, tak banyak perempuan, handsome, kaya, hero novel.\" Senarai panjang aku bagi.\r\n \"Well.. semua tu saya ada.\" Dia senyum.\r\n Bluerk! Tang banyak perempuan aje dah failed.\r\n Tapi itulah, jodoh tu rahsia Allah. Tak fasal kena kahwin dengan Tengku Fahd. Bermulalah ketenteraman hidup aku diganggu oleh perempuan-perempuan spesis tak boleh move on.\r\n Dah hari-hari jumpa mestilah akan jatuh cinta. Sampai kehadiran Moana buat aku goyah.\r\n \"I pregnant anak Fahd. Please.. kalau you tak boleh terima I tak apa.. you jaga anak i.. please Zalikha.\"\r\n Kusut, serabut, sakit hati, sakit jiwa.\r\n \"Sayang.. tu bukan anak abang..\"\r\n \"Then anak siapa? Anak Ika?\"\r\n Sebab tu aku takut nak sayang dan cintakan orang, takut disakiti.. perlu ke aku tinggalkan Fahd demi anak yang Moana kandung? Seorang anak perlukan keluarga yang sempurna..', '../ebooksimage/687d4f84adbdd_MY BAE.. TENGKU FAHD.jpg', 219, 0, 13.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(25, 25, 'Zati Mohd', 'BERDOSAKAH AKU', 'Tak mengandung perempuan salah..\r\nTak pandai jaga anak perempuan salah..\r\nTak ada jodoh perempuan salah..\r\nTak pandai masak perempuan salah..\r\nKenapa setiap kesalahan harus diletak atas kepala insan bernama perempuan.\r\nKalau dah nama menantu tu buat lah kerja..\r\nKalau dah tak boleh mengandung bagi suami tu kahwin lain..\r\nohh dia tak apa, tak ada anak.. biar dia buat.\r\nItulah Ayu.. nama yang indah tetapi tak seindah kehidupan. Usia perkahwinan selama\r\nempat tahun tanpa zuriat meletakkan Ayu sebagai wanita paling malang di dunia.\r\nNamun kerana ingin menjaga aib suami dan keluarga mentua semuanya di telan sendiri.\r\nHati yang terluka terpaksa dia ikhlaskan semua. Alasan tak punya zuriat menyebabkan\r\nAyu dibuang begitu sahaja.\r\nIkuti kisah Ayu.. adaptasi dari KRT / IIUM / KISAH BENAR insan terdekat dan\r\npenambahan sedikit fiksyen.\r\n\"Kami semua dah sepakat nak kahwinkan Saiful dengan Wanie, yela kamu kan tak boleh\r\nmengandung.\" - Makanjang yang kenenkan Saiful dengan Wanie.\r\n\"Basuh baju ni pakai tangan tau Kak Ayu, jangan pakai mesin ni baju mahal harga ratusratus.\r\nLagi satu kakngah cakap suruh bancuh susu anak dia. Yela kak Ayu kan takde\r\nanak.. ada banyak masa lapang.\" - Afiqah (adik ipar Ayu)\r\n\"Awak kahwin dengan saya.. kita balas dendam.. jangan tunjuk wanita tu lemah Ayu..\r\nawak sempurna dekat mata saya.\" - Hakim si sweet manis melecas', '../ebooksimage/687d4fc90ea8a_BERDOSAKAH AKU.jpg', 377, 0, 17.00, '0', '8', 2020, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(26, 26, 'Zati Mohd', 'KAU MASIH KUCINTAI', '\"Tolong aku please kali ni aje.. aku janji last.\" Gaya dia merayu-rayu dekat aku.\r\nFuh! Aku hela nafas panjang. Panjang dari Perlis sampai Johor.\r\n\"Kau ingat kita kembar.. muka kita sama.. semua benda aku nak cover kau? Tolong la\r\nwei kau nak bertunang sabtu ni kan? Flight kau hari khamis. Kau memang suka buat gila\r\nkan.\" Aku marah sungguh-sungguh.\r\n\"Please kau aje harapan aku. Kau tahu kan impian aku nak jadi angle untuk satu produk\r\ninternational di Turki. Aku dah hantar paperwork, diorang suka. Sekarang aku kena pergi\r\nuntuk saringan ujibakat pulak. Tolong lah please.\"\r\nAku diam.\r\nAku Farah Afiqah.. gadis yang simple, cuma letak compact powder dengan liptint pastu\r\ntudung aku belasah ikut dan janji sarung atas kepala. Boyfriend? Takde.. sebab fokus aku\r\nnak jadi.. hurm jadi apa? Haaa.. fokus hidup aku jadi tukang cover si kembar kalau dia\r\nada masalah. Contohnya macam sekarang.\r\nDia. Farah Atikah.. gadis yang complicated. Sebelum keluar harus bersiap 1 jam awal.\r\nDari foundation sampai lip matte harus pakai elok-elok. Tudung? Harus lilit terletak\r\ncantik kalau tak rumah ni boleh meletup sebab tudung tak jadi. Instafamous merangkap\r\nmodel muslimah. Jumlah pengikut dia 630k. Fokus hidup dia nak lebarkan pengaruh\r\nhingga ke tahap international. Boyfriend? Boyfriend dia anak orang paling kaya dekat\r\nMalaysia. Kamal Hadi aka Hadi.\r\nHadi.. jejaka impian gadis-gadis malaya.. Malaysia, Indonesia, Brunei, Singapore siapa\r\ntak kenal. Tapi suri hati dia hanya Farah Atikah si model muslimah yang cantik namati.\r\nHandsome, bergaya, maskulin, macho ala-ala Mario Casas.\r\nMacam mana aku si Farah Afiqah nak jadi Farah Atikah dan menyamar sebagai tunang\r\nHadi. Omg! Aku kena lari jugak ke? Atau stay dekat sini dan jadi Farah Atikah. Aku\r\nbuntu..', '../ebooksimage/687d502656b0a_KAU MASIH KUCINTAI.jpg', 325, 0, 20.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(27, 27, 'Zati Mohd', 'LURUHNYA CINTA DI BUMI KOREA', 'Bercinta dengan IDOL KPOP? korang tak percayakan? Tapi itulah realiti seorang gadis\r\nbernama Izznie Syahirah. Perkenalan yang tercipta bersama Lee Jong Hyun semasa\r\nIzznie berpindah ke sebuah apartment studio di wilayah Seoul. Perkenalan yang bermula\r\nsebagai jiran akhirnya merubah mereka kepada kekasih.\r\nTapi bila Izznie serahkan seluruh hidupnya pada Jong Hyun. Berita Lee Jong Hyun\r\nbermain cinta bersama Idol Hanna Lee mendapat liputan meluas hingga ke media\r\ninternasional. Tanpa tunggu penjelasan, Izznie balik ke Malaysia. Hidupnya berubah 360\r\ndarjah. Nak tunggu penjelasan apa. Nak jumpa Lee Jong Hyun pun susah.\r\nTiga tahun berlalu, Izznie kini bertugas sebagai seorang wartawan hiburan. Mama nak\r\njodohkan dia dengan Hafiz anak ustazah mana entah. Laju saja Izznie menolak kerana\r\nkisah silamnya.\r\nTiba suatu hari Tuan Loqman ketua jabatan minta Izznie membuat liputan khas sebuah\r\nkumpulan yang sekarang ni sangat terkenal di dunia. Kumpulan Wazzup. Izznie tolak\r\nsebab dia tak nak jumpa Lee Jong Hyun tapi Tuan Loqman paksa jugak. Dengan bagi\r\njaminan kenaikan gaji. Izznie paksa rela.\r\nTour Konsert in Malaysia. Hari yang ditunggu pun tiba. Izznie tak boleh tipu rasa hati\r\nbila dia masih lagi sayangkan Lee Jong Hyun. Masa konsert tu, Lee Jong Hyun nyanyi\r\nsatu lagu khas yang dia cipta untuk Izznie “still waiting for you”.\r\nBerbunga balikkah cinta Izznie untuk Lee Jong Hyun di Malaysia? Atau Izznie tetap ikut\r\nkata mamanya untuk belajar mencintai Hafiz walaupun hatinya tak rela?', '../ebooksimage/687d507651da2_LURUHNYA CINTA DI BUMI KOREA.jpg', 306, 0, 18.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(28, 28, 'Zati Mohd', 'YANG MENCINTAIKU', 'CURANG! Curang itulah sebuah pilihan bukanlah takdir yang sudah tertulis.\r\nPerkahwinan tanpa zuriat bukanlah suatu ujian yang mudah untuk semua pasangan.\r\nDihina, dicaci dan dicemuh adalah asam garam untuk mereka yang telah lama berkahwin\r\ntapi tidak dikurniakan zuriat.\r\nAkibat daripada masalah kesihatan yang dialami oleh Mia. Dia dijadikan alasan untuk\r\ndicurangi oleh Amir. Bahkan kecurangan Amir mendapat sokongan dari rakan-rakannya\r\nsendiri.\r\nHidup bermadu bukanlah sesuatu yang mudah. Bagi Mia untuk isteri pertama mengalah\r\nsudah tidak relevan di zaman moden ini. Dia bangkit kembali, segala harta, kemewahan\r\nyang disaji buat Amir di ambil semula. Berkali-kali dia meminta cerai Amir tidak mahu\r\nceraikannya akhirnya dia menuntut fasakh.\r\n\"Abang takkan ceraikan Mia. Abang tak dapat Mia. Tak ada siapa pun boleh dapat Mia.\r\nAbang akan gantung Mia tak bertali.\" - AMIR\r\n\"Sampai hati abang.. dah lah abang seksa hati dan perasaan Mia.. sekarang abang nak\r\nsekat pulak hak Mia. Apa dosa Mia dengan abang?\" MIA\r\nPerkenalannya dengan Azri duda anak 3 yang kematian isteri membuka ruang di hati\r\nuntuk dicintai. Kasih sayangnya tumpah pada 3 anak yatim itu.\r\n\"You deserve to be happy with your own way.\" AZRI.\r\nSelusuri kisah hidup seorang wanita yang bernama Mia di dalam YANG\r\nMENCINTAIKU.', '../ebooksimage/687d50bb714e7_YANG MENCINTAIKU.jpg', 260, 0, 18.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(29, 29, 'Zati Mohd', 'LIZZY', 'Sebuah kisah cinta yang penuh pengorbanan.\r\nTiga tahun sudah berlalu, Lizzy kembali semula ke negaranya untuk mencari Rayyan.\r\nBila sudah dirasa keadaan kembali normal dan kes jenayahnya ditutup dia kembali\r\nmenjadi Lizzy yang baru. Hubungannya dengan Rayyan makin hari makin\r\nmencambahkan benih - benih cinta.\r\nDalam masa yang sama perkenalan antara Lizzy dan Kim sepupu Rayyan juga semakin\r\nerat.\r\n“Tolong aku hantarkan makanan ini untuk Lizzy. Aku ada kerja la malam ni.” Bekas\r\nmakanan terisi burger di suakan kepada Kim.\r\nHinggalah suatu hari, Rayyan didapati hilang selama tiga hari. Operasi mencari dan\r\nmenyelamat segera di jalankan. Janji Lizzy pada Rayyan adalah untuk tidak mengulangi\r\naktivitinya tiga tahun lepas. Kini, dia terpaksa ingkar. Mana mungkin dia terpaksa\r\nmelihat sahaja tunangnya hilang tanpa di jejak.\r\n“Rayyan.. awak dekat mana, kita akan berkahwin lagi dua minggu sahaja Rayyan.\r\nTolonglah jangan buat saya macam ni.” Jaket hitam dikenakan pada badan.\r\nMelihat susuk tubuh Lizzy yang murung. Kim tidak sampai hati.\r\n“Izinkan saya untuk menggantikan tempat Rayyan menjadi suami awak.” Pandangan\r\nmata Kim tepat membunuh anak mata Lizzy.\r\nApa yang terjadi pada Rayyan? Mampukah Lizzy tidak menjadi seorang pendendam\r\nseperti tiga tahun lalu. Dan apakah Kim berjaya mendapatkan cinta Lizzy. Dan siapa\r\ndalang di sebalik kes penculikan Rayyan. Semuanya akan terjawab dalam..', '../ebooksimage/687d50f2c8f2d_LIZZY.jpg', 259, 0, 18.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(30, 30, 'Zati Mohd', 'NOAH ALEXANDRO', 'Noah Alexandro punya segalanya. Harta, pangkat, wajah, semua cukup lengkap kecuali satu CINTA.\r\n\r\nAsyiqah atau lebih mesra dengan panggilan Ieqa, tak punya apa-apa. Harta langsung tak ada. Pangkat? Bayang pun tak nampak. Wajah? Kirim salam ajelah tapi dia punya satu hati yang penuh dengan kasih sayang.\r\n\r\nMereka dipertemukan kerana satu perkara. Terdesak.\r\n\r\nNoah terdesak perlukan tunang untuk diperkenalkan kepada keluarganya di Sabah, manakala Ieqa perlukan kerja untuk membayar segala hutang untuk keperluan hidup.\r\n\r\n\"Saya bayar awak 3000 sebulan tak termasuk gaji yang awak terima sekarang. Jadi tunang saya. Tapi ada syaratnya..awak hanya bekerja sebagai tunang saya. No hard feeling. Maknanya awak tak boleh jatuh cinta dengan saya. Sebab awak bekerja sebagai tunang saya.\"\r\n\r\n\"Okey, saya setuju. Jadi tunang aje kan?\"\r\n\r\nKontrak dipersetujui antara mereka. Tanpa sedar ada hati yang mula jatuh cinta.\r\n\r\nMereka mula bermain tarik tali. Mulut menidak tapi hati semakin mekar dengan rasa cinta.\r\n\r\nMampu cinta antara Noah Alexandro dan Asyiqah menjadi satu?\r\n\r\nSebuah cerita romantik, love-hated relationship untuk bacaan anda.', '../ebooksimage/687d516e5b0a9_NOAH ALEXANDRO.jpg', 248, 0, 18.00, '0', '3', 2002, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(31, 31, 'Zati Mohd', 'PADA SUATU HARI', 'ROSA gadis cantik dan bijak dizaman moden dibagunkan balik pada zaman silam 1730. Terperangkap pada zaman dahulu kala. Menjadikan dia sebagai \'MAWAR\' anak kepada bendahara.\r\n\r\nMawar anak gadis kepada bendahara dalam istana. Seorang yang lembab, senang di buli. Namun kebangkitan Rosa sebagai Mawar merubah segalanya. Seorang yang kasar, ceria, senang bergaul.\r\n\r\nPertemuan tanpa sengaja yang sering terjadi antara Mawar dan putera Belawa anak isteri kedua dari mahajara Tiwahung menjadikan mereka sering berjumpa.\r\n\r\n\"Kau siapa ha?\" - Mawar aka Rosa.\r\n\"Kau tak kenal siapa aku?\" Jejaka kacak yang duduk diatas kuda tu jungkit kening.\r\n\"Ish.. tak ada masa nak kenal kau.. masalah aku pun banyak tak settle lagi.\"\r\nJejaka itu senyum.\r\n\r\n\"Jauh berjalan sampai dalam istana..\" jejaka itu peluk tubuh sambil berdiri depan Rosa.\r\n\"Cari ayah.\"\r\n\"Cari ayah atau cari saya?\" Dia senyum.\r\nOh shit! Kat mana-mana pun dia ada.\r\n\r\n\"Ayah kenalkan ini Putera Belawa.\"\r\nWhat? Putera? O-m-g\r\n\r\nDari pertemuan ke pertemuan itu wujud rasa cinta di hati mereka.\r\n\r\n\"Saya tak sanggup berpisah dengan awak Mawar. Jadi isteri saya?\" Putera Belawa.\r\n\r\nSehinggalah pada suatu hari, putera sulung Dekamatra yang tidak puas hati dengan Putera Belawa telah meracun Belawa dan jadikan Mawar sebagai isteri no 3 dia. Kerana sumpah setia. Mawar dan Belawa berjanji untuk jumpa pada kelahiran seterusnya.\r\n\r\n2019\r\n\r\nPertemuan tak dijangka antara Rosa (Mawar) dan Luth Mikael (Belawa) membawa kisah baru di zaman milenia. Bagaimana kisah hidup meraka? Adakah persis kisah Mawar dan Belawa di zaman dahulu?\r\n\r\n\"Tepi sikit nak lalu.\" Luth Mikael.\r\n\"Awak..awak.. Putera Belawa?\"\r\n\"Saya Luth Mikael. Bila masa pulak jadi Belawa?\" Dia tergelak kecil.\r\n\"Tapi saya macam pernah jumpa awak tapi tak ingat dekat mana..\" dia sambung lagi.\r\n\r\nMampukah mereka bersatu semula menuntut janji-janji yang telah dibina pada suatu ketika dahulu? Semuanya bakal terjawab dalam naskah Pada Suatu Hari.', '../ebooksimage/687d51cc29625_PADA SUATU HARI.jpg', 117, 0, 15.00, '0', '11', 2020, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(32, 32, 'Zati Mohd', 'TOLONG CINTAI AKU', 'Setiap hari disebalik pintu dia menanti kepulangan suaminya. Bila terdengar deruman enjin motorsikal laju kakinya berlari. Dan kekecewaan mula muncul.\r\n\r\nBaru seminggu bernikah Alesya kematian suami tercinta Akmal. Hakikat itu tidak dapat diterima oleh dirinya.\r\n\r\nKesunyian, kehilangan, kerinduan yang amat dalam itu yang dirasainya saat ini. Saban waktu Alesya menanti kepulangan Akmal.\r\n\r\nAkibat daripada tidak tahan melihat menantu tersayang sedih. Halijah meminta anak lelakinya iaitu Afiq abang kepada Akmal untuk menyamar menjadi suami kepada Alesya kerana muka Akmal dan Afiq seiras.\r\n\r\n\"Ibu ni merepek aje minta orang buat macam tu.. tak nak lah.. orang tak gila lagi ibu nak jadi Akmal.\"\r\n\r\nNamun saban hari pujukan Halijah didengari dan Afiq setuju untuk menjadi watak utama...', '../ebooksimage/687d521dc8e0f_TOLONG CINTAI AKU.jpg', 106, 0, 10.00, '0', '6', 2022, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(33, 33, 'Zati Mohd', 'JATUH CINTA LAGI', 'Ini kisah hidup aku. Sera seorang penulis novel merangkap penulis skrip yang memegang gelaran “janda”.\r\nBerkahwin dan menyintai lelaki bernama Seung Hyun. Perkahwinan tanpa restu. Impian aku adalah menjadi suri rumah tangga hancur berkecai. Bila kematangan tidak pernah menguasai kami. Dan kata sepakat memutuskan untuk kami bercerai. \r\nMenyesal? Sudah tentu. Tapi gelaran ini akan aku bawa sampai bila-bila. Namun keadaan merubah segalanya bila aku kembali berjumpa dengan dia. Choi Seung Hyun. \r\nMampukah aku seorang novelis merangkap penulis skrip jatuh cinta lagi dengan bekas suami aku seorang Hallyu star terkenal di Korea. \r\n•\r\n•', '../ebooksimage/687d53644ccc8_JATUH CINTA LAGI.png', 101, 0, 10.00, '0', '7', 2025, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(34, 34, 'Zati Mohd', 'OH SEPHIA', 'Sephia dicurangi berkali-kali namun dia masih setia dengan Kamal. Si lelaki buaya darat yang malas nak bekerja dan hanya habiskan duit perempuan. \r\nHinggalah suatu hari, Sephia dikejutkan dengan berita namanya di jadikan sebagai penjamin oleh Kamal yang membuat hutang disebuah agensi. Hidupnya tak tenang. Hinggalah dia terpaksa lepaskan butiknya bagi membayar hutang Kamal. \r\nSephia jatuh miskin. Keretanya ditarik, rumah berbulan tidak berbayar. Api dipotong. Hinggalah dia terpaksa bekerja sebagai clearner di salah sebuah hotel mewah. \r\nDisitu dia berjumpa dengan Anas Naufal. Lelaki yang paling dia benci dulu. Tapi apa boleh buat Anas Naufal pemilik hotel tersebut. Maka bermulalah episod benci, geram, sakit hati dan macam-macam lagi yang dilalui oleh Sephia. \r\nHingga Sephia terlupa. Hatinya makin jatuh cinta dengan Anas Naufal. Dalam benci ada cinta. Macam mana tu?\r\n•\r\n•', '../ebooksimage/687d539cd2e38_OH SEPHIA.jpg', 92, 0, 10.00, '0', '7', 2025, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(35, 35, 'Zati Mohd', 'TIDAK AKAN HILANG CINTAKU', 'Hidup dari tenang, gembira dan bahagia. Berubah menjadi mimpi buruk bila aku jumpa dengan Fareeq Danial anak kepada bos aku iaitu Dato Zahar. Tak fasal fasal jadi PA sementara si Fareeq tu. \r\nSikit pun dia tak bagi aku hidup dengan tenang.\r\n\"Darling awak tolong booking meja dekat W hotel saya nak dinner dengan Mawar pelakon tu. Make sure saya nak yang paling romantik.\" \r\nNama aku Aileen Darlina tapi bila dengan Fareeq dia  tukar jadi Aileen Darling. Apa bendalah dia ni. \r\n\"Darling orderkan saya dua puluh kuntum bunga ros merah. Saya nak pergi birthday party Atikah model Sephora tu.\" \r\nDari kerja pejabat sampai hal peribadi aku kena uruskan. Suatu hari aku dapat panggilan kecemasan. Ibu jatuh sakit dan aku terpaksa balik kampung haritu jugak. Atas ihsan si Fareeq ni dia hantar aku balik ke Baling. \r\nDua hari di kampung aku nampak sisi lain si Fareeq. Hormat orang tua, baik dengan orang kampung dan yang paling penting ibu senang dengan Fareeq. Ehhh sudah, banyak sangat memuji ni. \r\nDan siapa sangka jodoh aku dah tertulis. Aku kena tangkap basah dengan Fareeq. Kelakar! Tapi sebab berita dah tersebar. Dari baling ibu ke KL.. Dato Zahar terpaksa buat keputusan. Kami kena nikah. Fareeq menolak dan aku lagilah tak nak! \r\nFaliq pula buat keputusan gila. \r\n\"Kalau abang tak nak biar Faliq yang ganti. Faliq nak kahwin dengan Aileen.\" \r\nAku dalam dilema. Fareeq atau Faliq. Dan keputusan dah dibuat. \r\nDari PA kepada isteri. Aku yakin Fareeq takkan jatuh cinta dengan aku. Dan Faliq pulak masih dengan cintanya kepada aku. Arghhhhh! Sakit kepala.', '../ebooksimage/687d544477428_TIDAK AKAN HILANG CINTAKU.jpg', 167, 0, 15.00, '0', '2', 2025, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL);
INSERT INTO `ebooks` (`id`, `no`, `penulis`, `tajuk`, `description`, `file_path`, `muka_surat`, `perkataan`, `harga_rm`, `genre`, `bulan`, `tahun`, `penerbit`, `created_at`, `uploaded_by`) VALUES
(36, 36, 'Zati Mohd', 'KAU YANG AKU MAHU', '2010\r\n\"Lagi lima tahun kita jumpa okay Eira.\" \r\nItu ayat terakhir dari Iqbal kekasih zaman sekolah aku. Dia janji untuk setia bila pulang dari luar negara. Aku pegang kata-katanya. \r\nTahun pertama kami berhubung macam biasa, masuk beberapa bulan lepas tu kadang kadang saja kami berhubung. Dan akhirnya sepi tanpa berita aku yang beriya tunggu depan komputer sambil on Skype. \r\nDari lima tahun melarat ke tujuh tahun dan akhirnya sepuluh tahun.. aku masih tak dapat mencari Iqbal. Facebook tak ada, instagram pun tak nampak rupa tapi Iqbal sentiasa aku sematkan dalam hati. \r\nHinggalah suatu hari, aku dapat job besar majlis pertunangan anak salah seorang ahli perniagaan terhebat di Malaysia. Kayla. Kayla bertunang dengan Iqbal. \r\nYe Iqbal kekasih aku yang aku tunggu lamaaaa gila tu. Amarah aku mencanak. Aku tunggu dia berpuluh tahun dia pulak sedap sedap bertunang dengan orang lain. \r\nAku rancang plan, Khai bukan Khai Bahar okay abang kepala Kayla dan kawan baik kepada Iqbal. Rancangan aku hanya nak sakitkan hati Iqbal tak guna tu. \r\nJadi apa aku buat? Aku mengorat Khai. Tapi mamat tu jual mahal. Hampir rosak plan aku, aku bagitau aku suka dia, aku jatuh cinta pandang pertama dengan Khai. Bila Khai nampak aku dia lari, ah aku tak peduli aku ligan Khai. Runsing dia dengan aku. Mungkin dia menyampah sampailah dia cakap..\r\n\"Kalau nak jatuh cinta.. jatuh cinta ajelah.. tak payahlah hari-hari datang sini.\" Khai mendengus kasar. \r\n\"Awak jadi boyfriend saya tau.\" \r\n\"Yelah..yelah pergi balik..\" \r\nDan Khai buat kerja sebagai boyfriend aku. Selalu aku cakap dengan diri sendiri \"Eira kau nak sakitkan hati Iqbal aje, jangan nak jatuh cinta dengan si Khai ni.\" \r\nTapi boleh ke aku tahan perasaan dari jatuh cinta dengan Khai? Ahhhhhh tidakkkkkk!', '../ebooksimage/687d5969b1212_KAU YANG AKU MAHU.jpg', 181, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(37, 37, 'Zati Mohd', 'BUNGA – BUNGA CINTA', '\"Kenalkan.. anak angkat papa.\" \r\nTernganga mulutnya bila dengar anak angkat. \r\n\"Hai saya Ara Zafeera.\" Aku senyum dan kenalkan diri. \r\n\"Jangan nak mengaku adik aku. Dah tua bangka macam ni.\" Terus dia berjalan naik ke bilik. \r\nBerlagak gila!', '../ebooksimage/687d56eb7f76b_BUNGA BUNGA CINTA.jpg', 200, 0, 15.00, '0', '1', 2023, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(38, 38, 'Zati Mohd', 'BAHAGIA', '\"Mak abang tu kenapa ha? Bila balik jumpa dia aje suruh abang kahwin lain? Jangan sampai saya cari madu dekat mak abang tu.. saya kalau cakap saya buat..\" aku bagi amaran tegas dekat suami aku. \r\nMak mentua pun kalau asyik suruh laki aku cari bini lain. Siapa tahan? \r\n\"Awak ni dengan mak pun nak gaduh.. orang tua memang macam tu.. janganlah layan.\" Emir main main dengan remote tv.\r\n\"Habis yang abang tu pun takde function ke? Mak abang cakap apa diam je. Defend sikit bini abang ni. Ke abang suka kahwin lain? Haaa buat la kahwin lain.. abang keluar sehelai sepinggang dari rumah ni. Boxer pun saya tak bagi bawak.. togel je keluar rumah.\" \r\n\"Ya Allah ya tuhan.. tak ada pasai abang nak kahwin lain.. awak sorang pun dah serabut abang.\"\r\nHaaa tau pun bini sorang rasa serabut.', '../ebooksimage/687d58c6cd487_BAHAGIA.jpg', 160, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(39, 39, 'Zati Mohd', 'HELLO MR CEO', 'Apabila diupah menjadi pelakon untuk musnahkan hubungan antaran Eiman Rifqee dan Nisha. Aulia setuju. Kerja selesai bayaran pun dapat. Tapi Aulia lupa dia deal dengan salah orang.\r\nAulia Insyirah cari pasal dengan salah orang. Tak semena-semana Eiman pinang Aulia hanya kerana Aulia dah malukan dia.\r\n\"Lepas kau malukan aku.. kau buat semua family aku keliru pasal aku dah kahwin ke belum. Kau nak blah macam tu je? Aku tak kira.. kau kena bertanggungjawab. Aku nak kita kahwin lagi dua bulan.\" Garang dan tegas nada Eiman. \r\n\"Eeee encik Eiman bukan mengandung pun nak suruh saya bertanggungjawab bagai.. saya ni ada masa depan tahu tak.. saya nak kerja, kumpul duit beli kereta pulak. Kalau saya kahwin habis lah masa depan saya. Lagi pun encik Eiman ni siapa? Saya bukan kenal pun.\" Aku sandarkan badan dekat badan. \r\nTempoh tiga hari, habis macam tu aje tapi Aulia buat syarat.\r\n\"Pertama, saya tak nak duduk serumah dengan awak. Kedua, saya nak buat hal sendiri. Ketiga, lepas kahwin awak hal awak.. saya hal saya. Keempat awak boleh ceraikan saya bila bila masa awak suka.\" \r\nEiman setuju. Apalah sangat setakat perjanjian entah apa-apa Aulia ni. \r\nAkhirnya mereka dinikahkan, tapi buat hal masing-masinglah. Tapi hati manusia itu Tuhan yang pegang. Semakin hari semakin dekat dan mereka mulai mencintai satu sama lain. \r\nNamum dalam sesuatu hubungan ada pasang surutnya. Aulia salah faham.. Eiman pulak masih dalam fasa tak reti nak pujuk bini. \r\n\"Kita cerai ajelah.\" Aku tahan air mata. \r\nEiman diam. \r\nApa keputusan Eiman? Nak pujuk Aulia dia takut.. nak lepaskan Aulia.. dia sayang gila gila punya. Nak buat apa ni?', '../ebooksimage/687d5b2ed50bc_HELLO MR. CEO.jpg', 210, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(40, 40, 'Zati Mohd', 'KEKASIH LEBARAN', 'Dah masuk fasa bertunang nie. \r\nAku amati cincin di jari, ish betul ke keputusan aku nak jadi isteri Arash nie. Baru kenal sebulan lepastu dia ajak aku kahwin..\r\nMacam mana kalau dia ajak aku kahwin sebab.. sebab.. dia nak tutup yang dia tu gay? Ish apa kau merepek ni Dahlia. \r\nDari baring sebelah kiri, aku pusing pulak ke kanan.. tiba-tiba otak aku ni rajin pulak duk proses benda yang bukan-bukan dah tu macam mana aku nak tidur? Aku ambil telefon dan tengokn skrin yang tertera. \r\nSatu empat puluh pagi. Nak call ke tak nak? Kalau tak call aku tak boleh tidur. Call je la..\r\nTerus aku call Arash..\r\n\"Ada benda ni Lia nak tanya.\" Tak ada salam, tak ada mukadimah terus aku bagi dia pertanyaan. \r\n\"Penting ke Lia? Abang mengantok nie..\" suara dia serak-serak menandakan dia tengah tidur dan terpaksa bangun sebab aku kejut dia. \r\n\"Penting la.. kalau tak Lia tak boleh tidur.. otak Lia tak nak bagi kerjasama nie.\" \r\n\"Ye tanya la..\"\r\n\"Kenapa abang nak kahwin dengan Lia?\" \r\n\"Benda ni esok pun boleh tanya Lia.. esok lah abang jawab.\" \r\n\"Sekarang.. jawab sekarang.\" Aku paksa dia jawab\r\n\"Sebab abang sayang dan cintakan Lia.\" \r\nAku diam kejap. Sayang? Cinta?\r\n\"Tapi boleh ke jatuh cinta sekejap sangat, jumpa terus jatuh cinta.. bukan ke cinta tu memerlukan proses yang lama.. perlahan-perlahan jatuh cinta. Tak gitu?\" \r\nDiam.. \r\n\"Hello abang..\" aku panggil Arash..\r\nDiam.. \r\n\"Abang.. jawab la..\" \r\n\"Ye..ye.. abang cinta..\" \r\nApa yang abang cinta? \r\n\"Abang gay ke? Abang ajak Lia kahwin sebab nak tutup ke gay an abang tu kan? Baik abang mengaku.. Lia tak nak jadi isteri kepura-puraan abang macam dalam wattpad tu.. Lia tak sanggup nak pikul peranan jadi isteri tipu-tipu.\" \r\n\"Gaya abang ni macan gay ke? Banyak sangat tengok drama ni.. Lia, bagi abang tidur dulu esok abang jelaskan. Boleh?\" Suara dia macam dengar tak dengar je.. \r\n\"Hurm ye lah.\" Akhirnya aku pasrah..rupanya dia lebih memilih tidur dari aku. Huh!\r\nTapi ada aje kan cinta pandang pertama?', '../ebooksimage/687d5b63b9c8e_KEKASIH LEBARAN.jpg', 210, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(41, 41, 'Zati Mohd', 'TIKET KE NERAKA', 'Bagi Azmi pekerjaannya yang sekarang adalah syubhah dan dengan senang hati dia berhenti kerja sebagai penjawat awam dan ingin memulakan business sendiri.\r\n\r\nIna terbeban, baru tiga bulan nikah suaminya dah berhenti kerja. Dah tu janji nak cari rumah sewa terpaksa pulak duduk dengan mentua.\r\n\r\nDari gaji yang sebelum ni cukup untuk diri Ina kini dah jadi tak cukup. Sehinggakan duit ansuran Coway yang Ina bayar untuk emaknya pun jadi isu bagi Azmi.\r\n\r\n\"Enam puluh lima ringgit Coway yang awak bayar bulan- bulan boleh simpan untuk anak kita nanti. Membazir saja.\"\r\n\r\nSetiap hari Azmi makan tidur makan tidur. Tak ada duit mintak bini. Sampaikan mak Azmi naik menyampah. Nak marah anak sendiri. Habis siapa tempat dia nak luahkan marahnya kalau bukan Ina.\r\n\r\n\"Kau ingat tiga ratus yang kau bagi tu cukup ke? Bil elektrik saja pun dah dua ratus. Tambah lagi. Takkan orang tua macam aku nak bela kau pulak!\"\r\n\r\nIna tak tahan hidup tertekan. Dari cakap baik-baik kini dia dah pandai naik suara dekat Azmi. Hormat? Dah makin hilang. Yang ada hanya rasa benci..', '../ebooksimage/687d5bcb952dd_TIKET KE NERAKA.jpg', 181, 0, 15.00, '0', '1', 2023, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(42, 42, 'Zati Mohd', 'ATAS NAMA CINTA', 'Menjadi saksi utama kes pembunuhan berprofile tinggi membuatkan hidup Yara rasa\r\ndiburu. Dia diberi pilihan tutup mulut atau mati.\r\nKerana mahu melindungi saksi. Ean Maleek terpaksa melindungi Yara untuk dua\r\nminggu. Kata sepakat dibuat, Yara ditempatkan di kampungnya sementara waktu.\r\nMenyamar jadi kekasih Ean Maleek untuk dua minggu? Nak selamat punya fasal dan\r\ntak ada cara lain Yara setuju. Dan akhirnya Yara tiba dikampung Ean.\r\nDua minggu di kampung, Yara selesa.. dari hidup di bandar dia mula belajar cara\r\nhidup seperti di kampung. Bagi Ean pulak Yara ada satu tarikan yang membuatkan dia\r\nmula tak senang duduk bila Yara tiada.\r\nHingga suatu hari, Yara diculik.. Ean cuak. Misi mencari Yara bermula. Dari hasil\r\nsiasatan Yara diculik oleh orang Tan Sri. Ean tekad kalau apa-apa jadi pada Yara dia\r\ntakkan maafkan dirinya.', '', 163, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(43, 43, 'Zati Mohd', 'CINTA PERTAMA', 'Kerana terikat dengan hubungan keluarga mereka menyatu. Mulanya Afiq Hadeef cakap nak lari pergi Manchester dan perkahwinan ini takkan terjadi tapi lain cerita bila Afiq Hadeef muncul masa akad nikah, dengan alasan pasport kena bawak sorok. Nak tak nak kena kahwin jugak.\r\n\r\nBagi Anis Farhana, tidak rasa cinta buat Afiq Hadeef membuatkan hidupnya biasa saja. Tidur bilik asing asing. Hal hidup pun asing asing. Tapi kerana teringat dengan status dia mula anggap Afiq kawan baiknya. Kerana Afiq dah ada kekasih Anis tak mahu ganggu. Dah serupa perampas dia rasa.\r\n\r\nTapi kalau kekasih Afiq selalu mengelak dari jumpa, Anis jadi teman pengganti.\r\n\r\n\"Jom teman aku tengok wayang, awek aku last minit tak jadi pulak.\"\r\n\r\n\"Nak ikut aku hiking tak? Aku call awek aku tak angkat.\"\r\n\r\nTanpa sedar, Anis jatuh cinta. Layanan baik dari Afiq membuatkan hati wanitanya cepat cair. Namun kerana Anis tahu cintanya takkan terbalas. Dia simpan rasa itu. Suatu hari dia dikejutkan dengan khabar Afiq dah putus. Jadi kesempatan ini Anis ambik atau biarkan saja?', '../ebooksimage/687d5d423be79_CINTA PERTAMA.jpg', 172, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(44, 44, 'Zati Mohd', 'PATAH HATI', 'Patah hati Bella apabila mengetahui si kekasih hati bermain cinta dengan wanita lain\r\ndengan alasan wanita itu adalah pilihan emaknya. Dalam masa yang sama, teman baik\r\nBella iaitu Suraya mahu jodohkan Bella dengan abangnya. Dapatkah hati Bella yang\r\npatah itu dirawat semula?', '', 182, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(45, 45, 'Zati Mohd', 'TENTANG CINTA', 'Detik pertemuan pertama mereka apabila Ben Ashriq menerima tugasan pertama, dia\r\ndiminta untuk menculik Nada Tarina anak kepada Tuan Rafour. Dan Nada menikmati\r\ndetik-detik penculikannya.\r\n\"Saya nak makan dalam sungai.\" Nada sengih.\r\n\"Dalam rumah ni ada meja makan yang kau nak makan dekat dalam sungai macam\r\ndalam instagram tu kenapa? Dah satu hal pulak aku nak melayan.\"\r\n\"Ehhh sedar sikit awak tu tengah culik saya tahu tak. Jadi awak kenalah ikut cakap\r\nsaya.\"\r\nPertemuan kedua, di sebuah kafe.\r\n\"Awak boleh tolong saya tak? Jadi suami saya. Saya tak kenal lelaki lain, saya kenal\r\nawak aje.\"\r\nHanya satu jalan untuk keluar dari rumah besar milik Tuan Rafour iaitu kahwin.\r\n\"Apa aku dapat kahwin dengan kau?\"\r\n\"Apa saja yang awak nak.\" Nada bagi kata putus.\r\nBagi Ben Ashriq ini peluangnya. Peluang untuk dia kenal rapat dengan Tuan Rafour.\r\nPerjanjian dipersetujui. Dan ini tentang cinta antara Ben Ashriq dan juga Nada Tarina.\r\nAntara cinta dan pergolakan hidup.', '../ebooksimage/687d604c912e5_TENTANG CINTA.jpg', 230, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(46, 46, 'Zati Mohd', 'SEKEPING HATI DIBAWA BERLARI', 'Kerana trauma dengan kehidupan, Alyana pilih untuk terus membujang hingga tua.\r\nNamun siapa sangka diusia awal 30an dia dijodohkan dengan cucu kawan maktok.\r\nRaykal.\r\n\"Sorry apa nama kau tadi?\" Raykal pandang aku lama.\r\n\"Nurin Nealofa Fazura Anna Aishwarya Fatimah binti Abdul Kadir.\" Okay saja aku\r\ntipu nama aku.\r\nPuas menolak, puas merayu agar dia pilih jalan hidup sendiri. Maktok tetap mendesak.\r\nPilihan terakhir. Dia mintak Raykal pergi bawak diri ke mana-mana asalkan tak\r\nmuncul di hari pertunangan mereka.\r\nAlyana silap. Raykal tiba dengan sepersalinan baju melayu sedondon. Tampan hingga\r\nmenambat hati semua yg hadir. Kehadiran Raykal membuatkan Alyana menyampah.\r\n\"Kita bertunang ni syarat aje tau, lagi tiga bulan saya nak putuskan pertunangan kita.\"\r\nAku jungkit kening.\r\n\"Terserah. Aku follow the flow.\" Raykal masukkan kedua tangan di dalam poket\r\nseluar.\r\nFollow the flow yang tak berapa nak menjadi bilamana mereka sering dipertemukan\r\nbila maktok dan nenel Raykal buat rancangan.\r\n\"Yana dah cuba tapi tak boleh lah maktok, Yana nak putuskan pertunangan ni.\"\r\nTerpaksa aku jujur dengan maktok.\r\nMaktok diam.\r\nNamun setelah maktok pujuk, Alyana terpaksa menikahi Raykal. Lelaki yang dia\r\nhanya tahu nama. Umur berapa, kerja apa.. semua tak penting buat Alyana.\r\n\"Biar aku tolong kau hilangkan trauma tu, aku janji takkan duakan kau macam ayah\r\nkau buat dekat mak kau. Aku janji.\" Raykal senyum.\r\n\"Saya tak percaya.\" Susah untuk aku hilangkan rasa takut ni.\r\n\"Kalau aku berjaya robohkan tembok trauma kau apa aku dapat?\"\r\nRaykal mintak hadiah ke?\r\n\"Kau nak apa PS5? Holiday?\" Aku tanya dia.\r\nLaju dia geleng. \"Tu..\" dia letak jari telunjuk di dada aku. \"Hati kau.\"\r\n\"Kau nak hati aku buat apa? Jual?\" Muka aku dah merah tahan malu.\r\nNamun dalam membina kepercayaan Alyana kehadiran seseorang membuatkan\r\ntembok trauma kembali meninggi.\r\n\"Apasal tak cakap dia tu kekasih kau?\" Aku rasa nak lari balik rumah maktok\r\nsekarang.\r\nMampukah Raykal pecahkan tembok trauma yang dimiliki Alyana tentang kehidupan\r\nberumahtangga? Semuanya di dalam sekeping hati dibawa berlari.', '../ebooksimage/687d608d47128_SEKEPING HATI DIBAWA BERLARI.jpg', 180, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(47, 47, 'Zati Mohd', 'AKU BUKAN BONEKA', 'Kata orang yang menjadi mangsa rumah tangga itu adalah wanita, namun mereka\r\nlupa.. lelaki juga sama..\r\nPerkahwinan yang diimpikan bahagia oleh Faris musnah apabila campur tangan\r\nkeluarga mentuanya. Segala apa yang dilakukan Faris segalanya di bantah.\r\n\"Abah nak kau tinggal dengan abah.. rumah kau tu bagi aje kaklong sewa.. kau duduk\r\nsini Faris.\"\r\n\"Anita kalau boleh abang.. Anita tak nak anak lagi tau.. Anita tak ready.\"\r\n\"Abangcik pinjam kereta kau.. pinjam sekejap aje.\"\r\n\"Kaklong tak cukup duit nak bayar sewa.. tangguh dulu ye.\"\r\nFaris terbeban. Antara dayus dan hormat. Antara cinta dan ambil kesempatan.\r\nAkhirnya dia memilih untuk pergi..\r\nNamun, pergi dari hidup Anita sekeluarga bukan juga membawa bahagia. Tapi dia\r\nterbeban dengan hutang. Dia di isyitiharkan bankrup. Faris tidak punya jalan kecuali\r\nsatu bunuh diri. Baginya itu adalah jalan penamat.\r\nNamun begitukah kesudahan kisah Faris atau mungkin ada sinar cahaya yang\r\nmembantu?', '../ebooksimage/687d60c467afa_AKU BUKAN BONEKA.jpg', 147, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(48, 48, 'Zati Mohd', 'CINTA UNTUK MELODI', 'Bukan menjadi anak kesayangan itu perit. Sejak umurnya 16 tahun, Melodi mula\r\nmembanting tulang empat kerat demi kelangsungan hidup.\r\nAda emak hanya sayangkan abangnya. Bila umur mula meningkat ke 18 tahun.\r\nMelodi dipaksa bekerja untuk menampung hidup ibu dan abangnya si biawak hidup.\r\nSetiap hari Melodi mengambil upah menjual nasi lemak, bihun goreng, mee goreng\r\nserta kuih muih untuk diletak di merata gerai. Manakala bila tiada kelas Melodi\r\nmengambil upah sebagai pembantu rumah separuh masa.\r\nAyden, kehidupan yang serba lengkap serta mendapat cukup kasih sayang menjadikan\r\nhidupnya sentiasa bahagia.\r\nHinggalah suatu ketika, takdir menemukan mereka. Melodi dan Ayden terpaksa\r\ndisatukan atas nama suami isteri.\r\nTiada cinta. Status perkahwinan mereka terpaksa dirahsiakan demi nama baik\r\nkeduanya di kampus tempat mereka belajar.\r\n\"Aku tak suka kau rapat dengan Taufik, kau isteri aku.\" -Ayden\r\n\"Apasal? Awak cemburu? Saya bercinta dengan Taufik masa semester 1 lagi. Saya tak\r\nnak awak menyibuk dalam hidup saya.\" -Melodi.\r\nTanpa sedar, hadir satu perasaan antara mereka. Cinta.\r\n\"Habis apa keputusan awak? Cerai? Awak nak ceraikan saya?\" - Melodi\r\n\"Aku ikut kau aje.\" - Ayden\r\nWujudkah perasaan cinta untuk Melodi dalam diri Ayden?', '../ebooksimage/687d610264236_CINTA UNTUK MELODI.jpg', 251, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(49, 49, 'Zati Mohd', 'HEY MR CRUSH', 'Adira Safeeya dah lama crush dengan Emir Rayyan sejak pertemuan pertama mereka\r\ndi rumah Adira Safeeya dua tahun lalu dan kebetulan sekarang ini mereka satu\r\nUniversiti, makin berbunga bunga rasa cinta Adira Safeeya buat Emir Rayyan.\r\nEmir Rayyan lelaki yang agak terkenal di Universiti. Dia kacak, budak bola sepak,\r\nbudak pandai dan menjadi kegilaan para gadis di universitinya.\r\n\"Kau ni memang crush sangat ke dekat Emir tu?\" Linda siku aku bila kami sama-sama\r\nlepak dekat cafe lepas habis kelas.\r\n\"Aku ni dah jatuh cinta tergolek-golek dekat dia tau.. setiap malam aku mesti mimpi\r\ndia.. tengok la tu.. dia minum pun handsome.\" Tak berkelip mata aku pandang Emir\r\nyang tengah lepak dengan kawan-kawan dia.\r\nHinggalah pada suatu hari. Kerana janji kedua orang tua mereka. Emir Rayyan dan\r\nAdira Safeeya ditunangkan. Emir menolak tapi Adira terima.\r\n\"Aku nak kita rahsiakan hal pertunangan kita..\" Emir Rayyan ketuk jarinya di atas\r\nmeja.\r\n\"Okay boleh aje.. saya tak kisah janji kita bertunang.\" Pucuk dicita ulam mendatang.\r\nRezeki jangan ditolak. Aku sengih.\r\nIni peluang untuk Adira Safeeya dekat dengan Mr Crushnya. Setiap hari dia ambil\r\npeluang untuk dekat dengan Emir.\r\nTanpa diduga.. Emir minta mereka putuskan pertunangan. Kerana dia tidak mencintai\r\nAdira Safeeya. Hubungan fake menjadikan Emir tidak selesa.\r\nLima tahun berlalu.. mereka bertemu semula.. dan kalini Emir yang mengejar Adira.\r\nMasihkan ada rasa cinta dalam diri Adira buat Mr Crush nya? Atau mungkin Adira\r\ntelah lama buang jauh jauh nama Emir.', '../ebooksimage/687d613e04ee8_HEY MR CRUSH.jpg', 201, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(50, 50, 'Zati Mohd', 'HEY MR BAD GUY', 'Pertemuan yang tak dijangka.. pertama di Kay\'s cafe, kedua ketika di Viandra\r\nUniversiti.\r\nLily Qaisara - menjadi pelajar baru di Viandra Universiti taklah semudah yang dia\r\nsangka. Ada saja masalah bila terserempak dengan Qaiser Adrien.\r\nQaiser Adrien - Auranya luar biasa. Beku. Dingin. Namun sejak kemunculan Lily\r\nbagaikan satu magnet untuk dia terus memburu gadis itu.\r\nDan malam itu, segalanya merubah hidup Lily. Dia diculik hanya kerana Qaiser.\r\nKetika Lily dalam bahaya Qaiser hulurkan tangan.\r\n\"Be my wife. And i will take care the rest.\" - Qaiser\r\n\"Pardon?\" - Lily\r\nKerana tanggungjawab Qaiser menjaga Lily sepenuhnya tanpa mereka sedar ada cinta\r\nyang sedang mekar.\r\n\"Seronok nampak tergedik-gedik dengan si Laura tu.. dah lah pakai baju tak cukup\r\nkain. Terbuka sana terbuka sini. Yang awak tu pun bukan main lagi sedap mata\r\nmemandang.\" - Lily\r\n\"Jangan jatuh cinta dengan aku. Aku bukan lelaki yang baik.\" Qaiser\r\nSiapa Lily pada Qaiser? Dan siapa Qaiser yang sebenarnya?', '../ebooksimage/687d6177bc317_HEY MR BAD GUY.jpg', 153, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(51, 51, 'Zati Mohd', 'BAHAGIAKU DERITAMU', 'Apa anda rasa bila dituduh menyihir keluarga mentua sendiri?\r\nItulah yang terjadi pada Nisa. Dari mula dia berkahwin dengan Anuar ada saja yang\r\ntak kena. Demam sikit jumpa bomoh hinggakan semasa Nisa mengandung pun dia\r\nhampir dituduh dirogol jin hanya kerana hormon dia berubah.\r\nKeadaan semakin gila bilamana manusia lebih mempercayai benda lain selain Tuhan.\r\nNisa dituduh menyihir ibu mentuanya. Tanpa bukti yang munasabah. Nisa telah\r\ndiceraikan. Ipar duai dan biras semua menyokong tindakan Anuar.\r\nBukan saja Nisa malah seluruh keluarga Nisa pun dituduh bermain bomoh!\r\nNisa tekad melangkah pergi, dia harus bangkit semula. Wanita bukan lemah. Dia\r\nbangun untuk buktikan siapa Nisa sebenarnya. Anuar gila talak. Dia mahu Nisa\r\nsemula. Anuar janji untuk tidak mudah terpengaruh dengan keluarganya. Adakah Nisa\r\nakan menerima Anuar kembali?', '../ebooksimage/687d61ba77c30_BAHAGIAKU DERITAMU.jpg', 156, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(52, 52, 'Zati Mohd', 'SUJUD CINTA TERAKHIR', 'Namaku Sofia. Perempuan simpanan kepada Farid yang bersatus suami orang. Farid\r\nyang penyayang dan Farid yang suka mengikuti segala kemahuan aku. Aku\r\ndimanjakan dengan wang ringgit dan harta benda. Rumah, kereta, Prada, LV, Dior\r\nminta saja semua Farid tunaikan. Dan lagi best bila Farid berjanji akan mengahwini\r\naku. Itu janjinya.\r\nPerjalanan yang aku rasa bukan level aku untuk ke Aceh, mempertemukan aku\r\ndengan Ustaz Aiman salah seorang pelajar Malaysia di Aceh yang menjadi AJK dalam\r\nprogram Rentas Kasih anjuran Universiti aku. Pertama kali bertentang mata\r\ndengannya buat aku jadi gugup, berdebar.. entahlah ada daya tarikan dari seketul\r\nAiman. Wajahnya bersih senyumannya manis. Gempa bumi yang terjadi di Aceh\r\nmembuatkan aku melihat lebih dalam sisi seorang Aiman. Tanpa sedar aku jatuh cinta.\r\nAh gila!\r\nAku tekad nak tinggalkan Farid. Ketika aku buat keputusan ini. Diri aku viral. Gambar\r\naku disebarkan di media sosial sebagai tajuk utama babi penyondol oleh isteri Farid.\r\nAku dihina, dicaci dan dimaki. Abah yang sememangnya membenci aku terus\r\nbertambah bencinya buat aku. Aku malu! Demi menebus harga diri aku, Farid akan\r\nmenikahi aku dan jadikan aku isteri kedua. Tapi aku tak cintakan Farid. Aku menolak.\r\nFarid mengugut aku. Jadi adakah aku harus menerima Farid atau tekad mencari\r\nAiman? Korang rasa?', '../ebooksimage/687d61f242c28_SUJUD CINTA TERAKHIR.jpg', 150, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(53, 53, 'Zati Mohd', 'IZINKAN AKU PERGI', 'DIkala rumahtangga yang diimpikan bahagia hingga ke syurga hadir wanita lain\r\nmenjadi perosak. Kehadiran Milea di dalam rumahtangga Azura dan Haziq\r\nmenjadikan hidup Azura huru hara.\r\nDalam diam Haziq menjalinkan hubungan cinta dengan Milea, kerana Milea disahkan\r\nmengandung Haziq mengahwini Milea. Mereka bernikah di siam. Dalam terpaksa\r\nHaziq terpaksa memberitahu Azura kehadiran Milea.\r\n\"Sampai hati abang buat saya macam ni. Baru 2 tahun kita nikah abang.\" Aku tak\r\nmampu mengeluarkan lagi kata-kata.\r\nSungguh hati aku sakit. Sakit sangat.\r\n\"Abang minta maaf Zura. Kami dah terlanjur lagipun ni zuriat yang abang nak..\"\r\nHancur hati Azura. Ya kekurangan dirinya memberi kelebihan buat suami untuk\r\ncurang.\r\n\"Terima kasih kak izinkan Milea tumpang rumah akak sampai beranak. Milea janji tak\r\nkacau akak dan abang ya.\"\r\nJanji tinggal janji.. bila..\r\n\"Abang jom pergi makan Milea nak sangat makan Nandos\"\r\n\"Abang.. malam ni teman Milea erh.. baby nak cakap dengan papa dia.\"\r\n\"Abang.. tak sedaplah kak Zura masak.. eee bluerkkk.\"\r\nDan akhirnya Azura pasrah.. melangkah pergi bukan bermakna mengalah. Tapi\r\nmemberi peluang untuk diri sendiri di hargai.\r\nMembawa diri hingga ke Phuket. Mempertemukan Azura dan Kashaf.\r\n\"Saya Kashaf.. bujang, single dan kacak.\"\r\nMampukah si jejaka kacak ini merawat luka di hati Azura?', '../ebooksimage/687d62358cd8e_IZINKAN AKU PERGI.jpg', 139, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(54, 54, 'Zati Mohd', 'DIHATIMU BUKAN ADA AKU', 'Salahkah untuk si isteri cemburu dengan birasnya sendiri bilamana si suami memberi\r\nperhatian sepenuhnya kepada isteri si adik?\r\nItulah yang terjadi pada Sha apabila suaminya E sering menghabiskan masa bersama\r\nisteri adiknya iaitu Zura. Bila ditegur..\r\n\"Itukan adik ipar saya apalah awak nak cemburu sangat.\"\r\n\"Kesian si Zura tu Que asyik kerja tak ada masa nak bawa dia keluar beli barang.\"\r\n\"Zura masakkan untuk saya.. dia panggil balik rumah mak.\"\r\nSha tak tahan bilamana perhatian yang seharusnya dia dapat beralih arah kepada Zura.\r\nSatu persatu masalah timbul bila munculnya si Zura ini dalam hidup Sha dan Ezzudin.\r\n\"Abang E belikan gelang ni katanya hadiah harijadi. Tapi tak adalah besar macam kak\r\nSha.\"\r\nNantikan.. DIHATIMU BUKAN ADA AKU', '../ebooksimage/687d6273d17da_DIHATIMU BUKAN ADA AKU.jpg', 169, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(55, 55, 'Zati Mohd', 'MY EVERYTHING', 'Qayra anak kepada seorang tauke penyeludup senjata haram. Pada dasarnya Tuan\r\nAbdol adalah pemilik hotel lima bintang yang terkenal di negaranya namun disisi\r\ngelap dia adalah penyeludup senjata haram.\r\nQayra yang manis, cantik, bijak dan yang paling penting the sexy diva.. mendapat\r\ngelaran sebagai the party queen kerana tiap bulan gemar menganjurkan parti tapi\r\npartinya bersih tiada arak dan seks.\r\nArjuna atau Mr President orang terpenting kepada Tuan Abdol, rakan kongsi The\r\nAbdol Resort and Hotel dan sleeping partner untuk penyeludupan senjata haram.\r\nDalam masa yang sama body guard kepada Qayra.\r\n\"Tu kau punya prince charming dah datang.\" Beberapa rakan yang lain dah tersengih\r\nmacam kerang busuk.\r\nQayra angkat kepala dan pandang wajah Arjuna yang serius gila tu.\r\nDalam samar-samar cahaya lampu di kelab malam ini pun dia dapat perhati wajah\r\nArjuna.\r\n\"Balik!\" Tangan Qayra di tarik oleh Arjuna.\r\n\"Tak nak lah!\" Qayra tegas, masih keraskan diri dekat atas kerusi.\r\n\"Balik sekarang Qayra, apahal degil sangat ni!\" Arjuna mendegus kasar.\r\nHinggalah suatu masa, mereka terpaksa di kuarantin bersama. Kerana urusniaga\r\nsenjata haram dibatalkan dengan negara luar, maka Qayra menjadi sasaran.\r\n\"Ayah akan tempatkan Qayra dekat Abdol Resort di pulau Mantan bersama-sama\r\nArjuna.\"\r\n\"No way! Qayra tak nak stay dengan mamat ni. I hate him.\"\r\nMampukah Arjuna untuk menjaga keselamatan Qayra? Dan bagaimana pula dengan\r\nQayra bila 24 jam terpaksa menghadap wajah tampan si Arjuna. Segalanya akan\r\nterjawab dalam MY EVERYTHING.', '../ebooksimage/687d62c573a76_MY EVERYTHING.jpg', 170, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(56, 56, 'Zati Mohd', 'KETIKA AKU JATUH CINTA', 'Apa rasa apabila pengantin lelaki tidak hadir di majlis pernikahan. Marah? Kecewa?\r\nDikhianati? Malu? Maruah tercalar?\r\nItulah yang dirasakan oleh Ammara apabila Faris tidak hadir ke majlis pernikahan\r\nmereka. Tanpa penjelasan Faris menghilangkan diri. Hanya sebuah mesej \"im sorry for\r\neverything\" sebagai pengganti diri.\r\n8 tahun berlalu..\r\nKini Ammara dipertemukan kembali dengan Faris.\r\n\"Kenalkan ini bos baru awak.. Faris ni Ammara PA kamu.\"\r\nMenjadi PA kepada Faris membuatkan hidup Ammara kucar kacir. Bila dia sering\r\nmendesak Faris dengan sebuah penjelasan, Faris membalas dengan mendiamkan diri.\r\nTak habis rasa sakit hati terhadap Faris kini muncul Sakinah.. tunang Faris.\r\n\"Apa kata kalau awak yang tolong saya cari barang kahwin.. awak PA Faris kan?\r\nMesti dia bagi saya pinjam PA dia sekejap. Jom.\"\r\nDulu Faris meninggalkan Ammara di majlis pernikahannya kini, Ammara pulak\r\nmenjadi wedding planner tak bertauliah buat Faris.\r\n\"Awak masih cintakan saya kan?\" -Faris\r\n\"Hello.. saya dah move on okay.. apalah sangat tak jadi nikah dengan awak.\" -\r\nAmmara\r\n\"Faris tu cinta pertama saya. Saya sayangkan dia sangat-sangat.\" Sakinah\r\nMelepaskan Faris terlalu sukar untuk dilakukan. Namun melihat Faris bahagia adalah\r\ndendam dalam hidupnya.\r\nAmmara.. loving you like a losing game. Jadi jangan pernah mengalah bak kata\r\nbudak-budak PUBG Winner Winner Chicken Dinner', '../ebooksimage/687d63012657f_KETIKA AKU JATUH CINTA.jpg', 139, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(57, 57, 'Zati Mohd', 'THE BACHELOR', 'Renata Illiyin - Dingin, dia punya dingin sebeku iceberg.\r\nHenry - Nakal, dia punya nakal sampai Renata pun meluat nak layan.\r\nMereka berdua bertemu dalam rancangan THE BACHELOR. Henry jejaka yang perlu\r\nmemilih si gadis untuk dijadikan pasangan hidup.\r\nMatanya tak lepas memandang Renata, gadis iceberg yang susah nak mesra dengan\r\nmana-mana jejaka termasuk dia.\r\n\"Awak ni lesbian erh?\"\r\nRenata yang tengah bersiap untuk photoshoot pusing 360 darjah.\r\n\"Ulang balik apa awak cakap tadi.. rasa macam saya ni pekak pulak.\"\r\nKejap! Aku tak salah dengarkan? Renata pandang tepat anak mata Henry macam nak\r\nmakan.\r\n\"Awak ni lesbian ke? L-e-s-b-i-a-n.\" Henry eja satu-satu perkataan lesbian tu.\r\nOmg! Anak siapa ni? Tolong ambik sebelum kena lempang.\r\n\"Apa sebab awak cakap saya lesbian?\" Renata peluk tubuh.\r\n\"Sebab awak tak layan lelaki. Nak senyum pun payah. Saya yang ulang alik depan\r\nawak, langsung awak tak kisah. Kalau perempuan lain rajin pulak tegur saya.\" Henry\r\ntarik kerusi duduk merapati Renata.\r\nRenata buat muka annoying.\r\n\"Saya bukan tergolong dalam perempuan macam tu. Lagipun harga diri saya terlalu\r\nmahal, Encik Henry yang kacak gila ni pun tak mampu nak beli saya.\" Renata senyum\r\ngaya mengejek.\r\n\"Really.. kalau saya mampu beli harga diri awak tu.. apa awak janjikan dekat saya?\"\r\nHenry dekatkan wajahnya pada Renata.\r\nAyatnya lembut dan perlahan.\r\n\"Saya janjikan seluruh hidup saya untuk Encik Henry.\" Renata kenyitkan matanya.\r\nHaaa nampak macam lesbian lagi ke? Kali ni kita guna watak playgirl. Biar Henry\r\njatuh tersungkur. Auch!\r\nNamun bila Henry dah jatuh cinta, dia ditinggalkan begitu saja. Gadis iceberg itu\r\nbenar-benar buat dia kecewa. Sedingin itukah hati Renata terhadap dia.\r\nBenarkan Renata tidak mencintai Henry? Dan adakah Henry akan melepas Renata\r\npergi begitu saja. Semuanya dalam THE BACHELOR.', '../ebooksimage/687d6341ef4db_THE BACHELOR.jpg', 133, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(58, 58, 'Zati Mohd', 'DIA KESAYANGAN DARIEN', 'BERKAHWIN atas dasar cinta? Tak!\r\nBerkahwin atas dasar sayang? Tak!\r\nBerkahwin atas dasar paksaan? Ya!\r\nMalam pertama yang tak seindah orang lain. Aku Dikurung dirumah agam mewah\r\nmilik ahli keluarga Darien. Makan di hantar oleh pembantu rumah. Aku hanya\r\ndibenarkan menikmati angin segar hanya diri balkoni.\r\nDarien masuk bersama jaket kulit hitam dan berseluar jeans.\r\n“Sampai bila kau nak kurung aku Darien?” satu jelingan tajam menikam seluruh\r\nwajah Darien.\r\nKebencian yang makin membuak-buak. Kalau boleh aku bunuh Darien. Sudah pasti\r\ndah lama tubuh Darien aku tikam-tikam.\r\n“Sampai aku rasa bosan dengan kau.” Darien buka baju.\r\nAku pusing belakang apabila Darien buka seluar pula.\r\n“Perempuan lain semua gila nak tengok tubuh aku.. kau berpaling kenapa?”\r\n“Tu perempuan lain. Bukan aku!”\r\n“Lambat laun kau mesti gilakan aku jugak.” Tubuh aku dipeluk dari belakang.\r\n“Pergi mampus!” Tangan Darien aku tolak kasar.\r\nMati hidup balik pun aku takkan jatuh cinta dengan kau!', '../ebooksimage/687d63951155d_DIA KESAYANGAN DARIEN.jpg', 136, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(59, 59, 'Zati Mohd', 'TERUNA MISTERI', 'Seorang writer yang nama pasaran dia \"teruna\" menjadi kegilaan para remaja dengan\r\nimej misteri, seksi, cool, hebat, gaya mutu keunggulan.\r\nTapi.. pada Qisha dia tak kisah wujud atau tak Teruna sebab dia memang tak minat\r\nmembaca. Teruna ke duda ke bujang trang tang tang lantak lah langsung dia tak kisah.\r\nTapi yang di kisah bilaaaa si Faizul curang dengan Killa pendek.\r\nEeeee.. rasa geram pulak bila rahsia curang Faizul dengan Killa pendek sampai dekat\r\ntelingan dia.\r\nMasa kesedihan Qisha melanda suatu hari di ruang pintu Qisha terdengar suara lelaki\r\nmenyanyi bersama petikan gitar. Siap lelaki tu menjerit lagi\r\n\"Jiran-jiran boleh request lagu apa nak..esok saya menyanyi untuk semua. Tapi tak\r\nboleh buka pintu ya. Letak tajuk lagu di depan pintu rumah anda\"\r\nQisha tertarik. Dia pun request lagu.. hahaha adoi.. suara lelaki tu buat Qisha cair.\r\nSampai suatu hari..\r\n\"Mama nak jodohkan Qisha dengan anak kawan mama dia baru balik dari London, dia\r\nkerja sana.. Qisha okey?\"\r\nHurm. Nak terima ke tak?', '../ebooksimage/687d63cd3c9a3_TERUNA MISTERI.jpg', 112, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(60, 60, 'Zati Mohd', 'DEAR BESTFRIEND', 'Crush dengan bestfriend sendiri? Itulah perasaan Carl ketika ini. Menyintai rakan\r\nbaiknya sendiri secara senyap-senyap. Bagi Nadine nak jatuh cinta dengan Carl itu\r\nmustahil.\r\nMelindungi, menyanyangi dan menjaga Nadine adalah tanggungjawab Carl. Namun\r\ntanpa sedar Nadine turut sama menyintai Carl dalam diam.\r\nMekarkah kisah cinta mereka?', '../ebooksimage/687d640b0f3a6_DEAR BESTFRIEND.jpg', 143, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(61, 61, 'Zati Mohd', 'DALAM SANGKAR CINTA', 'Kerana cinta aku butakan semua apa yang Ikhwan buat. Tujuh tahun perkahwinan\r\nyang kadang kala bertelagah aku hanya anggap biasa. Hingga suatu hari, Ikhwan\r\nkantoi bermain cinta dengan kekasih lama.\r\n“Dia ex girlfriend abang, abang minta maaf.. hati abang masih ada pada dia.”\r\nAku terduduk, jatuh dan tersungkur. Inilah titik hidup aku yang paling teruk sekali.\r\n“Kalau macam tu ceraikan saya.” Aku bagi kata putus.\r\n“Tak.. abang takkan lepaskan sayang. Abang masih perlukan awak dan anak-anak.”\r\nTamak. Itu perangai Ikhwan, aku dia mahu.. perempuan itu pun dia mahu. Jujur hati\r\naku sakit. Hati aku tawar. Tujuh tahun yang aku korbankan hanyalah sia-sia.', '../ebooksimage/687d644021c83_DALAM SANGKAR CINTA.jpg', 154, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(62, 62, 'Zati Mohd', 'RETAK MENANTI BELAH', 'Perkenalan hanya sebulan membawa dirinya ke alam perkahwinan. Setahun pertama,\r\nsemuanya indah namun bila masuk tahun kedua. Rumah tangganya sukar dikawal.\r\n“Jom kita pindah duduk rumah mak awak.” Azrul bersahaja dan nampak tenang.\r\n“Rumah yang awak beli ni kenapa? Kenapa nak pindah keluar pulak?” Aku tak puas\r\nhati.\r\n“Bapak dengan adik beradik saya nak tumpang duduk sini. Adik beradik saya lelaki,\r\ntakkan awak nak duduk dengan diorang. Sekejap aje. Dua minggu.”\r\nDalam terpaksa akhirnya aku reda. Melangkah keluar dari rumah sendiri hanya kerana\r\nuntuk keselesaan yang lain.\r\nMula kata dua minggu kini melarat ke dua bulan. Azrul pula dah tak bagi aku nafkah\r\nalasan adik beradiknya perlu di bantu.\r\nDuduk di rumah keluarga sendiri, sedikit sebanyak mula rasa menyusahkan.\r\n“Mak.. kaklong tak cukup duit nak bagi mak bulan ni. Sebab nak cover bayar hutang.”\r\nterpaksa aku tebalkan muka bagitahu mak.\r\n“Tak apalah kak long, apa mak makan.. kau makanlah. Mak tak boleh masak sedap\r\nsedap macam laki kau bersungut tu. Kita bukan orang senang.”\r\nAku telan air liur. Mak dengar?\r\nAtirah tak puas hati, dia mula mengungkap kebenaran. Rumah itu milik siapa\r\nsebenarnya? Kemana semua duit Azrul?\r\nTAJUK : DALAM SANGKAR CINTA (bacaan dewasa mengandungi kemakian yang\r\nagak kasar)\r\nKerana cinta aku butakan semua apa yang Ikhwan buat. Tujuh tahun perkahwinan\r\nyang kadang kala bertelagah aku hanya anggap biasa. Hingga suatu hari, Ikhwan\r\nkantoi bermain cinta dengan kekasih lama.\r\n“Dia ex girlfriend abang, abang minta maaf.. hati abang masih ada pada dia.”\r\nAku terduduk, jatuh dan tersungkur. Inilah titik hidup aku yang paling teruk sekali.\r\n“Kalau macam tu ceraikan saya.” Aku bagi kata putus.\r\n“Tak.. abang takkan lepaskan sayang. Abang masih perlukan awak dan anak-anak.”\r\nTamak. Itu perangai Ikhwan, aku dia mahu.. perempuan itu pun dia mahu. Jujur hati\r\naku sakit. Hati aku tawar. Tujuh tahun yang aku korbankan hanyalah sia-sia.', '../ebooksimage/687d647e64011_RETAK MENANTI BELAH.jpg', 150, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(63, 63, 'Zati Mohd', 'DIMATAMU AKU, DIHATIMU DIA', 'Suhana mula curiga apabila suaminya Firdaus membelanjakan wang syarikat dengan\r\nbegitu mewah sekali. Bahkan Firdaus selalu travel atas urusan perniagaan.\r\nHinggalah suatu hari, Suhana mendapat tahu mengenai hubungan sulit suaminya\r\ndengan Amelia. Suhana memberi Firdaus pilihan. Antara keluarga ataupun Amelia.\r\nNamun, Firdaus tetap dengan keputusannya untuk memiliki keduanya.\r\nSuhana harus bijak. Strategi diatur. Dia akan musnahkan impian Firdaus untuk terus\r\nmemiliki dirinya. Firdaus harus membayar kebahagiaan dirinya yang telah hancur\r\nberkecai.', '../ebooksimage/687d64ffb9b8e_DIMATAMU AKU, DIHATIMU DIA.jpg', 200, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(64, 64, 'Zati Mohd', 'HAQ ANAQI', 'Kerana cinta ditolak oleh Lalisa.. Haq Anaqi tekad untuk buktikan pada wanita itu dia\r\njugak mampu dicintai.. bahkan dia akan buktikan bahawa kekasihnya nanti akan\r\ndilayan bagaikan puteri.\r\nPertemuannya dengan Nelysa Haura membuka peluang pada dirinya untuk\r\nmenjadikan gadis itu sebagai pelakon utama drama yang bakal dia cipta.\r\nKata sepakat dibuat.. bermula episod drama cinta arahan Haq Anaqi. Tanpa sedar\r\nmuncul rasa cinta yang sebenar namun hati Haq menolak rasa itu.\r\nHinggalah suatu hari, Lalisa membawa berita bahawa dia telah putus tunang dan\r\nmeminta Haq untuk memberi peluang buat dirinya semula.\r\nDan disitu tamatlah sudah drama cinta Haq dan Nelysa. Masing-masing membawa\r\ndiri. Tapi bagi Haq melupakan Nelysa itu sukar. Antara Lalisa dan Nelysa Haura dia\r\nperlu memilih.', '../ebooksimage/687d6532861fa_HAQ ANAQI.jpg', 123, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(65, 65, 'Zati Mohd', 'NELYSA HAURA', 'Macam mana rasa kalau boyfriend kita ada kawan baik perempuan lepastu berkepit 24\r\njam. Kadang-kadang kita pulak rasa macam perampas. Itulah rasa Nelysa Haura bila\r\nHaziq banyak berkepit dengan Erika aka best friend forever.\r\nKerana tak sengaja terlanggar kereta AMG milik Haq Anaqi dia terperangkap dalam\r\ndrama cinta lelaki itu. Terpaksa dia rahsiakan status kekasih lakonan Haq daripada\r\npengetahuan Haziq.\r\nSaban hari bertemu Haq Anaqi sedikit sebanyak tercuit rasa dalam hati. Namun cepat\r\nsaja dia menolak. Hingga suatu hari Haq Anaqi tamatkan drama antara mereka dengan\r\nalasan Lalisa sudah kembali kepada dirinya.\r\nAda rasa terluka terhiris sedikit di dalam hati. Dapat pula berita Haziq ditangkap\r\nkhalwat bersama Erika menambah lagi luka itu. Dari terhiris sedikit kini terbelah lagi.\r\nNelysa membawa diri tapi jiwanya lebih terseksa bila memikirkan Haq Anaqi.\r\nHinggalah dia dipertemukan kembali dengan Haq Anaqi.', '../ebooksimage/687d656bc3e69_NELYSA HAURA.jpg', 133, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(66, 66, 'Zati Mohd', 'THE KINGDOM', 'Hanya kerana mahukan tahta kerajaan Kingdom, Loth terpaksa turun ke bumi untuk\r\nmencari gadis perawan sebagai tahanannya. Dikenali sebagai the death angel panahan\r\nmatanya mampu membunuh jiwa jiwa yang penuh rasa cinta.\r\nGrisella seorang gadis biasa, hidup di dalam kemiskinan kais pagi makan pagi, kais\r\npetang makan petang. Hidupnya hanya untuk si ibu yang sedang terlantar sakit.\r\nHatinya penuh kasih sayang yang suci.\r\nPertemuan pertama antara Loth dan Grisella, di pasar tani. Sedang Grisella membelek\r\nbunga ros putih tubuhnya di langgar dari belakang. Lelaki bertubuh tegap itu tidak\r\npernah dia lihat. Namun pandangan pertama mula mengikat mereka.\r\nPertemuan kedua, ketika bandar tempat Grisella tinggal mengadakan malam pesta ria\r\nsemperna sambutan bulan cerah. Grisella duduk menatap bulan di pinggir sungai\r\nhingga dia terjumpa Loth terbaring penuh dengan kecederaan. Rasa kasihan yang\r\nmendalam Loth diberi rawatan.\r\nDan Loth tawarkan Grisella untuk mengikutnya. Menjanjikan yang ibu Grisella akan\r\nsihat sepenuhnya. Grisella rela. Apa pun demi ibunya.\r\nBermulalah detik di mana dua jiwa terikat dengan rasa cinta. Panahan mata Loth tidak\r\nmampu untuk membunuh jiwa Grisella yang sedang mencintai bahkan mampu untuk\r\nmenambah lagi rasa cinta dalam dirinya.\r\nSiapakah sebenarnya Grisella buat Loth?', '', 135, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(67, 67, 'Zati Mohd', 'SEMALAM YANG HILANG', 'Hidup ini ada dua antara hidup dan mati.. tapi tidak bagi irdina dia di tengah-tengah\r\nhidup tidak mati pun tidak. Dia roh yang terkapai-kapai mencari arah jalan untuk\r\ndituju.\r\nIrdina, Kemalangan ngeri yang menimpa dirinya menjadikan dia seperti sekarang.\r\nRoh yang setia ditepi jasad tanpa tahu haluan seterusnya.\r\nAirel, seorang detektif yang sedang menyiasat kes kemalangan waris tunggal Iris\r\nJewellery. Tanpa diduga hanya dia seorang saja yang mampu melihat roh Irdina.\r\n\"Awak kena tolong saya.. saya nak balik ke dunia.. saya tak nak jadi macam ni. Awak\r\nkena siasat siapa yang nak saya mati.\" - Irdina\r\n\"Kau boleh aje duduk dalam rumah ni mana-mana yang kau suka tapi bukan bilik\r\naku.\" - Airel.\r\nDengan adanya Irdina, sedikit demi sedikit kes kemalangan itu hampir terungkai.\r\nHinggalah suatu kejadian. Dia tidak temui lagi roh Irdina.\r\n\"Bertahan, aku akan pastikan kau selamat Irdina. Aku tak izinkan kau mati.\" - Airel', '../ebooksimage/687d6693bae72_SEMALAM YANG HILANG.jpg', 130, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(68, 68, 'Zati Mohd', 'SALAHKAH AKU', 'Mulanya hadir dengan rasa ikhlas kini jadi satu bebanan dalam hidup Fina apabila dia\r\nyang kehulu kehilir menjaga keluarga mentuanya sedangkan yang lain hanya lepas\r\ntangan.\r\nMak mentua yang panas baran. Bapak mentua pulak kuat mengadu apa saja Fina buat\r\nsemua tak betul bukan itu saja siap tambah lagi cerita yang entah datang dari mana.\r\nAdik beradik ipar? Semua lepas tangan. Suami pula, tak habis-habis ayat \"minta\r\ntolong\"\r\nSampai suatu hari Fina keguguran. Dia mengalami kemurungan yang teruk. Apa saja\r\nyang dia lakukan dimata keluarga mentuanya hanya keburukan. Hingga Khair\r\ntermakan kata-kata mereka.\r\nHinggalah suatu hari kehadiran Isaac membuka mata Fina bahawa dia sebenarnya\r\ndibuli.\r\nMampukah Isaac membantu Fina keluar dari neraka dunia yang dicipta Khair dan\r\nkeluarganya?', '../ebooksimage/687d66d1235ef_SALAHKAH AKU.jpg', 150, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(69, 69, 'Zati Mohd', 'CINTA SAMPAI MATI', 'Anda yang pernah membaca YANG MENCINTAIKU mesti kenal dengan Azri dan\r\nRos jom baca kisah mereka di dalam Cinta Sampai Mati\r\nAssalamualaikum dan hai\r\nCinta pandang pertama mampu membawa kepada cinta pertama.\r\nIya itu Azri rasakan saat matanya menatap sepasang mata bundar yang cantik milik\r\nRos. Hatinya sudah terpikat namun kerana status Ros sebagai anak orang gaji. Azri\r\ntakut cintanya ditolak. Lagipun Ros bukan tipikal gadis yang senang di ayat.\r\n\"Jom saya belanja goreng pisang. Saya ada dua ringgit ni.\" Azri senyum sambil hulur\r\nnot dua ringgit.\r\n\"Saya pun ada, saya boleh beli sendiri.\" Ros berlalu tinggalkan Azri sendirian.\r\nLagi Azri mendekat lagi Ros menjauh. Hingga Azri tekad menyatakan rasa cintanya\r\nkepada Ros biarpun Ros akan menolak.\r\n\"Nak dengar tak saya nyanyi lagu?\" Petikan gitar mula berbunyi. Azri menatap mata\r\nRos dengan penuh rasa cinta.\r\n\"Lagu apa? Burung kakak tua?\"\r\n\"Lagu cinta kita.\"\r\nDan seperti yang dijangka Ros memang punya hati pada Azri tapi Ros buang jauhjauh\r\nhanya kerana dia anak orang gaji. Pada Ros semakin dia menolak Azri semakin\r\ngalak Azri mendekat. Bukan sekadar mendekat malah sering buat dia terpikat. Dan\r\nRos selalu sangat jatuh. Jatuh cinta.\r\n\"Kita tak boleh kahwin Azri. Awak anak orang kaya. Orang terkenal. Saya ni orang\r\nbiasa aje. Kita memang tak layak bersama. Saya anak orang gaji. Kurang serba serbi.\"\r\n\"Ada saya kisah?\"\r\nMampukah Azri yakinkan Ros tentang cintanya dan mampukah cinta mereka\r\nberkekalan?', '../ebooksimage/687d6718805c1_CINTA SAMPAI MATI.jpg', 143, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(70, 70, 'Zati Mohd', 'ANGGAPLAH DIRIKU TAK PERNAH ADA', 'Membuka pintu untuk rakan baik sendiri adalah satu kesalahan terbesar dalam hidup\r\nLina.\r\nKemunculan Azra pada lewat malam tu membawa malapetaka yang besar pada Lina.\r\nSuaminya digoda rakan baik sendiri. Mampukah Lina mempertahankan rumah\r\ntangganya bersama Yusof.', '../ebooksimage/687d6758c0183_ANGGAPLAH DIRIKU TAK PERNAH ADA.jpg', 148, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(71, 71, 'Zati Mohd', 'DAN CINTA INI', 'Pernikahan Elnaura dan Lutfi a.k.a Luke ditentukan atas permintaan keluarga masing-masing. Bagi Luke pernikanannya dengan Elnaura seolah-olah memberi sinar untuknya mendapat hak di dalam Empires Group.\r\n\r\nBagi Elnaura pulak pernikahannya dengan Luke adalah jackpot untuk ayahnya melebarkan perniagaan di tanah Bali Indonesia.\r\n\r\n\"Kau tolong kahwin dengan aku dan aku ikut cakap kau.\" - Luke\r\n\r\n\"Eee takkan aku nak tidur dengan kau. Seumur hidup aku.. aku tak pernah sekatil, sebilik dan serumah dengan lelaki..\" - Elnaura.\r\n\r\nDihadapan keluarga masing-masing mereka terpaksa tunjukkan kemesraan. Kerana setiap tingkah laku mereka diperhatikan.\r\n\r\n\"Orang tengah tengok kita tu.. pegang tangan aku.\" Luke\r\n\r\nNamun kata orang tak kenal maka tak cinta. Setiap hari bersama membuka ruang ruang cinta di hati mereka berdua.\r\n\r\n\"Siapa perempuan tu? Bukan main lagi menggedik erh.\" Elnaura.\r\n\r\n\"Kalau aku dah jatuh cinta dengan kau macam mana?\" Luke.\r\n\r\nPercintaan yang mula dibina takkan terlepas dari ujian. Vincent Jeremy bekas kekasih Elnaura membuka rahsia tentang perjanjian pernikahan mereka.\r\n\r\n\"Aku minta maaf.. aku terpaksa tinggalkan kau.. aku nikah dengan kau pun sebab nak dapatkan hak aku dalam Empires Group. Maafkan aku Elnaura.\" Luke.\r\n\r\nElnaura terasa diperbodohkan. Hatinya hancur berkecai.. terasa bodohnya mencintai lelaki yang pentingkan diri sendiri. Selamat tinggal Luke.\r\n\r\nKemudian Luke kembali mencari Elnaura. Bagi Elnaura cintanya untuk Luke dah terkubur mati bersama cinta mereka yang dulu.\r\n\r\nJadi Elnaura ni nak lagi ke dekat Luke? Luke tu pandai ke pujuk perempuan?', '../ebooksimage/687d684fe9b14_DAN CINTA INI.png', 170, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
(72, 72, 'Zati Mohd', 'HEY MR SWEET TALKER', 'Maira seorang chef media sosial.. pertemuan yang tak dijangka dengan Faliq, pak cik Nisa yang long long time no see menyebabkan perkenalan mereka semakin rapat. Oh... ini bukan pakcik tua ya tapi yang muda bergetah.\r\n\r\nDari kota Kuala Lumpur hingga ke kampung Faliq mengekornya. Tingkah laku dan ayat Faliq yang sweet tu menyebabkan Maira jatuh cinta.\r\n\r\nNamun bila hati dah suka Maira disedarkan yang Faliq ni terlalu \'mat salleh\' untuknya. Semakin dia menjauh... semakin tak keruan hatinya.\r\n\r\nJadi apa Maira nak buat dengan pakcik long long time no see ni?', '../ebooksimage/687d69148159f_HEY MR SWEET TALKER.png', 125, 0, 15.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL);
INSERT INTO `ebooks` (`id`, `no`, `penulis`, `tajuk`, `description`, `file_path`, `muka_surat`, `perkataan`, `harga_rm`, `genre`, `bulan`, `tahun`, `penerbit`, `created_at`, `uploaded_by`) VALUES
(73, 73, 'Zati Mohd', 'SESAL YANG TERLEWAT', '“Saya Farhan Aziz bin Ahmad menceraikan Nur Alia binti Malik dengan talak satu.”\r\nTiga kali Farhan sebut talak satu. Bermakna jatuh talak tiga ke atas Alia, isterinya.\r\nSemuanya kerana mereka tidak memperolehi zuriat walaupun sudah 8 tahun berkahwin.\r\n“Kenapa tiga Farhan?” - Alia\r\n“Sebab aku tak nak kau rayu ayu. Aku nak kahwin dengan Safiya dan bina keluarga dengan\r\ndia. Kau tak mampu bagi aku keluarga.” – Farhan\r\nFarhan mahu membina hidup baru dengan pilihan emaknya. Mahu memberikan emaknya\r\ncucu tanpa gangguan dan rayuan Alia di kemudian hari. Oleh sebab itu dia jatuhkan talak\r\ntiga sekali gus. Namun kuasa Allah menentukan sesuatu yang lain.\r\n“Kak Alia mengandung dua bulan.” – Faezah\r\nTergemap Farhan dengan berita yang adiknya bawakan. Mahkamah sudah mengesahkan\r\ntalak tiga yang dia jatuhkan. Tak mungkin dia dapat kembali kepada Alia. Zuriat yang\r\nmereka nantikan selama ini muncul juga. Namun talak tiga bermakna tiada jalan untuk\r\nmereka kembali bersatu. Menyesal? Tiada gunanya. Masa tidak akan dapat diundurkan\r\nkembali.\r\n“Lepas awak bersalin. Saya akan cari cina buta. Saya nak kita kahwin balik. Tolong setuju.\r\nSaya betul-betul menyesal lafazkan talak tiga.” – Farhan.\r\nAdakah Alia akan mengikuti kemahuan Farhan?\r\nAdakah mereka berdua akan Kembali bersatu?', '../ebooksimage/687d698d65941_270 SESAL YANG TERLEWAT.jpeg', 140, 0, 11.00, '0', '', 0, 'Kejou Book Publication', '2025-07-20 19:29:41', NULL),
